// Get current date and time
const now = new Date();
const today = now.toISOString().split('T')[0];
const currentHour = now.getHours();

// Get all itinerary data
const itineraryData = $input.all();

// Filter trips for today and upcoming reminders
const todayTrips = itineraryData.filter(item => {
  const tripDate = item.json['Trip Date'];
  const departureTime = item.json['Departure Time'];
  const reminderHours = item.json['Reminder Hours'] || 24;

  // Check if trip is today
  if (tripDate === today) {
    // Parse departure time
    const [hours, minutes] = departureTime.split(':');
    const departureHour = parseInt(hours);

    // Check if we need to send reminder
    const hoursUntilTrip = departureHour - currentHour;

    // Send reminder if within reminder window
    if (hoursUntilTrip > 0 && hoursUntilTrip <= reminderHours) {
      return true;
    }
  }

  return false;
});

// Also get tomorrow's trips for evening prep
const tomorrow = new Date(now);
tomorrow.setDate(tomorrow.getDate() + 1);
const tomorrowDate = tomorrow.toISOString().split('T')[0];

const tomorrowTrips = itineraryData.filter(item => {
  return item.json['Trip Date'] === tomorrowDate && currentHour >= 18; // After 6 PM
});

// Combine today's reminders and tomorrow's prep
const tripsToProcess = [...todayTrips, ...tomorrowTrips];

return tripsToProcess.map(item => ({
  json: {
    ...item.json,
    reminderType: todayTrips.includes(item) ? 'today' : 'tomorrow',
    currentTime: now.toISOString()
  }
}));

// Get trip data and traveler contacts
const tripData = $('Filter Today\'s Trips').all();
const travelerContacts = $input.all();

// Create reminder messages for each trip
const reminders = [];

tripData.forEach(tripItem => {
  const tripInfo = tripItem.json;

  // Find travelers assigned to this trip
  const assignedTravelers = travelerContacts.filter(traveler => {
    const travelerTrips = traveler.json['Assigned Trips'] || '';
    return travelerTrips.includes(tripInfo['Trip Name']);
  });

  // Create reminder for each assigned traveler
  assignedTravelers.forEach(traveler => {
    const reminderMessage = tripInfo.reminderType === 'today' 
      ? `‚úàÔ∏è Travel Reminder: Your "${tripInfo['Trip Name']}" trip departs at ${tripInfo['Departure Time']} today!\n\nüìç From: ${tripInfo['Departure Location'] || 'TBD'}\nüìç To: ${tripInfo['Destination'] || 'TBD'}\n‚úàÔ∏è Flight/Transport: ${tripInfo['Flight Number'] || 'TBD'}\nüè® Accommodation: ${tripInfo['Hotel'] || 'TBD'}\n\n‚ö†Ô∏è Please arrive at the departure point 2 hours early. Don't forget your passport and documents!`
      : `üìÖ Tomorrow's Trip: "${tripInfo['Trip Name']}" departs at ${tripInfo['Departure Time']}\n\nüìç From: ${tripInfo['Departure Location'] || 'TBD'}\nüìç To: ${tripInfo['Destination'] || 'TBD'}\n‚úàÔ∏è Flight/Transport: ${tripInfo['Flight Number'] || 'TBD'}\nüè® Accommodation: ${tripInfo['Hotel'] || 'TBD'}\n\nüí° Tip: Pack tonight and prepare your documents!`;

    reminders.push({
      json: {
        travelerName: `${traveler.json['First Name']} ${traveler.json['Last Name']}`,
        travelerEmail: traveler.json['Email'],
        travelerPhone: traveler.json['Phone'],
        tripName: tripInfo['Trip Name'],
        departureTime: tripInfo['Departure Time'],
        tripDate: tripInfo['Trip Date'],
        departureLocation: tripInfo['Departure Location'],
        destination: tripInfo['Destination'],
        flightNumber: tripInfo['Flight Number'],
        hotel: tripInfo['Hotel'],
        tripDuration: tripInfo['Duration (Days)'],
        reminderType: tripInfo.reminderType,
        message: reminderMessage,
        preferredContact: traveler.json['Preferred Contact'] || 'email'
      }
    });
  });
});

return reminders;

