  `;

  return {
    json: {
      to: data.travelerEmail,
      subject: subject,
      body: data.message,
      html: htmlBody,
      travelerName: data.travelerName,
      tripName: data.tripName
    }
  };
});

return emailsToSend;

// Prepare SMS data for travel reminders
const reminderData = $input.all();

const smsToSend = reminderData.map(item => {
  const data = item.json;

  const smsMessage = data.reminderType === 'today'
    ? `‚úàÔ∏è Travel Reminder: "${data.tripName}" departs at ${data.departureTime} today from ${data.departureLocation || 'TBD'}. Arrive 2 hours early! Flight: ${data.flightNumber || 'N/A'}`
    : `üìÖ Tomorrow: "${data.tripName}" departs at ${data.departureTime} from ${data.departureLocation || 'TBD'}. Pack tonight! (${data.tripDuration || '?'} days)`;

  return {
    json: {
      to: data.travelerPhone,
      message: smsMessage,
      travelerName: data.travelerName,
      tripName: data.tripName
    }
  };
});

return smsToSend;

// Get existing log data
const existingLogs = $input.all();

// Get sent reminders data
const sentEmails = $('Prepare Email Reminders').all() || [];
const sentSMS = $('Prepare SMS Reminders').all() || [];

// Create log entries for sent reminders
const newLogEntries = [];

// Log email reminders
sentEmails.forEach(email => {
  newLogEntries.push({
    'Log ID': 'LOG-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9),
    'Timestamp': new Date().toISOString(),
    'Traveler Name': email.json.travelerName,
    'Trip Name': email.json.tripName,
    'Contact Method': 'Email',
    'Contact Info': email.json.to,
    'Status': 'Sent',
    'Reminder Type': 'Travel Reminder',
    'Message Preview': email.json.subject
  });
});

// Log SMS reminders
sentSMS.forEach(sms => {
  newLogEntries.push({
    'Log ID': 'LOG-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9),
    'Timestamp': new Date().toISOString(),
    'Traveler Name': sms.json.travelerName,
    'Trip Name': sms.json.tripName,
    'Contact Method': 'SMS',
    'Contact Info': sms.json.to,
    'Status': 'Sent',
    'Reminder Type': 'Travel Reminder',
    'Message Preview': sms.json.message.substring(0, 50) + '...'
  });
});

// Combine existing logs with new entries
const allLogs = [...existingLogs, ...newLogEntries];

return allLogs.map(item => ({ json: item }));
