const items = $input.all();

const isEnglish = (val) => {
  if (typeof val !== 'string') return false;
  const v = val.toLowerCase();
  return v === 'en' || v === 'english' || v.startsWith('en-') || v.startsWith('en_');
};

const out = items.map(({ json: src }) => {
  let A = Number(src.A ?? 0);  // select the A-th English track (0 = first)
  let i = 0, res = null;

  while (true) {
    const lang = src[`subtitles.${i}.language`];
    if (lang === undefined) break;

    if (isEnglish(lang)) {
      if (A === 0) {
        res = {
          index: i,
          language: lang,
          srt: src[`subtitles.${i}.srt`],
          srtUrl: src[`subtitles.${i}.srtUrl`],
        };
        break;
      }
      A--;
    }
    i++;
  }

  return { json: res ?? { index: null, language: null, srt: null, srtUrl: null } };
});

return out;

const START = 0;
// ================================
if (!Array.isArray(items)) return items;

const n = items.length;
const s = Number.isFinite(START) && START >= 0 ? START : 0;

if (s >= n) return [];           

return items.slice(s);
