// Get the AI response
const aiContent = $input.item.json.message.content;

// Get form data
const formTrigger = $('Meeting Notes Form').item.json;

const formData = {
  company_name: formTrigger["Company Name / Project Name"],
  meeting_notes: formTrigger["Paste in your meeting notes here"],
  asana_project_id: formTrigger["Asana Project ID"],
  slack_channel: formTrigger["Slack Channel Name"] || "#general"
};

// Parse extracted data
const extracted = {
  key_decisions: aiContent.key_decisions || [],
  action_items: aiContent.action_items || [],
  next_meeting_date: aiContent.next_meeting_date || null,
  pain_points: aiContent.pain_points || [],
  budget_mentioned: aiContent.budget_mentioned || null
};

// Create one item per action item with meeting context
const outputItems = extracted.action_items.map(item => ({
  json: {
    task: item.task,
    assignee: item.assignee,
    due_date: item.due_date,
    company_name: formData.company_name,
    meeting_notes: formData.meeting_notes,
    asana_project_id: formData.asana_project_id,
    slack_channel: formData.slack_channel,
    key_decisions: extracted.key_decisions,
    pain_points: extracted.pain_points,
    next_meeting_date: extracted.next_meeting_date,
    budget_mentioned: extracted.budget_mentioned
  }
}));

return outputItems;

