// Get aggregated tasks
const aggregatedData = $input.item.json;
const tasks = aggregatedData.Tasks || [];

// Get meeting context
const parseData = $('Structure Data').first().json;
const company_name = parseData.company_name || "Unknown Company";
const slack_channel = parseData.slack_channel || "#general";
const key_decisions = parseData.key_decisions || [];
const pain_points = parseData.pain_points || [];
const next_meeting_date = parseData.next_meeting_date;
const budget_mentioned = parseData.budget_mentioned;

// Format action items
const actionItems = tasks.map(task => ({
  name: task.name,
  assignee: task.assignee ? task.assignee.name.split('@')[0] : "Unassigned",
  due_date: task.due_on,
  url: task.permalink_url
}));

// Build Slack message
let message = `*üìù Meeting Summary: ${company_name}*\n\n`;

if (key_decisions.length > 0) {
  message += `*‚úÖ Key Decisions:*\n`;
  key_decisions.forEach(d => message += `‚Ä¢ ${d}\n`);
  message += `\n`;
}

if (actionItems.length > 0) {
  message += `*üìã Action Items Created (${actionItems.length}):*\n`;
  actionItems.forEach(item => {
    const dueDate = item.due_date ? ` - Due: ${item.due_date}` : "";
    message += `‚Ä¢ <${item.url}|${item.name}> - ${item.assignee}${dueDate}\n`;
  });
  message += `\n`;
}

if (pain_points.length > 0) {
  message += `*‚ö†Ô∏è Pain Points:*\n`;
  pain_points.forEach(p => message += `‚Ä¢ ${p}\n`);
  message += `\n`;
}

if (budget_mentioned) {
  message += `*üí∞ Budget: ${budget_mentioned}*\n\n`;
}

if (next_meeting_date) {
  message += `*üìÖ Next Meeting: ${next_meeting_date}*\n\n`;
}

const projectName = tasks[0]?.projects?.[0]?.name || "Asana";
message += `_Project: ${projectName}_`;

return [{ json: { channel: slack_channel, message: message } }];
