// n8n Code node — Run Once for All Items
// Goal: From multiple news API payloads, return up to 10 *distinct-topic* articles,
//       each with a valid URL and image, excluding Biztoc and known aggregators.
//
// Input shape (per item):
//   { json: { status, totalResults, articles: [ { title, url, urlToImage, source, publishedAt, description } ] } }
// Output shape (array of n8n items):
//   [ { json: { topicKey, title, url, image, source, publishedAt, description } }, ... ]


// ──────────────────────────────────────────────────────────────────────────────
// 1) Helpers & constants
// ──────────────────────────────────────────────────────────────────────────────

/**
 * Domains considered aggregators / press-release wires to SKIP entirely.
 * Note: MarketWatch press releases are matched via path check.
 * Extend this set as needed (e.g., add your own blacklist domains).
 */
const aggregatorDomains = new Set([
  'biztoc.com','techmeme.com','news.ycombinator.com','flipboard.com',
  'finance.yahoo.com','consent.yahoo.com','news.yahoo.com',
  'globenewswire.com','prnewswire.com','businesswire.com',
  'marketwatch.com/press-release','medium.com','substack.com','thefly.com'
]);

/**
 * Extract normalized hostname from a URL (without "www.")
 * @param {string} url
 * @returns {string} hostname or '' on failure
 */
function getHostname(url) {
  try {
    const u = new URL(url);
    return u.hostname.replace(/^www\./,'').toLowerCase();
  } catch { return ''; }
}

/**
 * Extract pathname from a URL in lowercase.
 * @param {string} url
 * @returns {string} pathname or ''
 */
function getPath(url) {
  try { return new URL(url).pathname.toLowerCase(); } catch { return ''; }
}

/**
 * True if the URL belongs to an aggregator or a press-release page.
 * Items matching this are SKIPPED during collection.
 * @param {string} url
 * @returns {boolean}
 */
function isAggregator(url) {
  const host = getHostname(url);
  const path = getPath(url);
  if (!host) return true;
  if (aggregatorDomains.has(host)) return true;
  if (host === 'marketwatch.com' && path.includes('/press-release')) return true;
  return false;
}

/**
 * True if the article is from Biztoc (by source name or hostname).
 * These are SKIPPED during collection.
 * @param {object} article
 * @returns {boolean}
 */
function isBiztoc(article) {
  const src = (article?.source?.name || '').toLowerCase().trim();
  const host = getHostname(article?.url || '');
  return src === 'biztoc.com' || host === 'biztoc.com';
}

/**
 * Sanitize title to a comparable, topic-level string.
 * Strips brackets/parentheses, common suffixes, and punctuation; collapses spaces.
 * @param {string} [title='']
 * @returns {string}
 */
function cleanTitle(title = '') {
  let t = String(title).toLowerCase();
  t = t.replace(/\([^)]*\)/g, ' ').replace(/\[[^\]]*\]/g, ' ');
  t = t.split('|')[0];
  t = t.split(' — ')[0];
  t = t.split(' - ')[0];
  t = t.replace(/\b(opinion|analysis|update|breaking)\b/g, ' ');
  t = t.replace(/[^a-z0-9\s]/g, ' ').replace(/\s+/g, ' ').trim();
  return t;
}

/**
 * Build a canonical topic key from cleaned title + registrable domain.
 * @param {object} article
 * @returns {string}
 */
function canonicalKey(article) {
  const titleKey = cleanTitle(article.title || '');
  const host = getHostname(article.url || '');
  return `${titleKey}__${host ? host.split('.').slice(-2).join('.') : ''}`;
}

/**
 * Parse date string to epoch ms (0 if invalid).
 * @param {string} s
 * @returns {number}
 */
function parseDate(s) {
  const d = new Date(s);
  return isNaN(d.getTime()) ? 0 : d.getTime();
}

/**
 * Heuristic quality score (aggregators already excluded earlier):
 *  - Reward presence of image
 *  - Reward longer description (capped)
 *  - Light recency bias
 * @param {object} a
 * @returns {number}
 */
function qualityScore(a) {
  const hasImage = a.urlToImage ? 1 : 0;
  const descLen = (a.description || '').length;

  return (
    hasImage * 5 +
    Math.min(descLen, 800) / 160 + // up to +5
    parseDate(a.publishedAt) / 1e12 // modest recency factor
  );
}


// ──────────────────────────────────────────────────────────────────────────────
/* 2) Collect & pre-filter all articles across inputs
      Keep only items that:
        - have URL AND image
        - are NOT Biztoc
        - are NOT from aggregators (isAggregator)
        - are NOT Yahoo consent redirect pages
*/
// ──────────────────────────────────────────────────────────────────────────────
const allArticles = [];
for (const item of items) {
  const arts = item.json?.articles ?? [];
  for (const a of arts) {
    if (!a?.url || !a?.urlToImage) continue;           // must have URL + image
    if (a.url.includes('consent.yahoo.com')) continue;  // skip consent redirects
    if (isAggregator(a.url)) continue;                  // skip aggregators entirely
    if (isBiztoc(a)) continue;                          // skip Biztoc entirely
    allArticles.push(a);
  }
}


// ──────────────────────────────────────────────────────────────────────────────
// 3) Group by canonical "topic" key to avoid duplicates across sources
// ──────────────────────────────────────────────────────────────────────────────
const groups = new Map();
for (const a of allArticles) {
  const key = canonicalKey(a);
  if (!key) continue;
  if (!groups.has(key)) groups.set(key, []);
  groups.get(key).push(a);
}


// ──────────────────────────────────────────────────────────────────────────────
// 4) For each topic, pick the best representative article (highest qualityScore)
// ──────────────────────────────────────────────────────────────────────────────
const picks = [];
for (const [key, arr] of groups.entries()) {
  const sorted = arr.slice().sort((a, b) => qualityScore(b) - qualityScore(a));
  const best = sorted.find(a => a?.url && a?.urlToImage);
  if (!best) continue;

  picks.push({
    topicKey: key,
    title: best.title || '',
    url: best.url,
    image: best.urlToImage,
    source: best.source?.name || getHostname(best.url),
    publishedAt: best.publishedAt || null,
    description: best.description || null,
  });
}


// ──────────────────────────────────────────────────────────────────────────────
// 5) Sort by recency and take the 10 most recent topics
// ──────────────────────────────────────────────────────────────────────────────
picks.sort((a, b) => parseDate(b.publishedAt) - parseDate(a.publishedAt));
const top10 = picks.slice(0, 10);


// ──────────────────────────────────────────────────────────────────────────────
// 6) Return as n8n items — all guaranteed to have URL + image and not aggregators
// ──────────────────────────────────────────────────────────────────────────────
return top10.map(x => ({ json: x }));
