// AI-powered lead scoring algorithm
const items = $input.all();
const scoredItems = [];

for (const item of items) {
  const data = item.json;
  let score = 0;
  let qualificationNotes = [];

  // Email domain scoring (business vs personal)
  const email = data.email || '';
  const domain = email.split('@')[1] || '';
  const personalDomains = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com'];

  if (!personalDomains.includes(domain.toLowerCase())) {
    score += 25;
    qualificationNotes.push('Business email domain (+25)');
  } else {
    qualificationNotes.push('Personal email domain (0)');
  }

  // Company size scoring
  const companySize = data.companySize || data.company_size || '';
  if (companySize.includes('50+') || companySize.includes('100+') || companySize.includes('500+')) {
    score += 30;
    qualificationNotes.push('Large company size (+30)');
  } else if (companySize.includes('10-50') || companySize.includes('25-99')) {
    score += 20;
    qualificationNotes.push('Medium company size (+20)');
  } else {
    score += 10;
    qualificationNotes.push('Small company size (+10)');
  }

  // Budget scoring
  const budget = data.budget || data.estimatedBudget || '';
  if (budget.includes('$10,000+') || budget.includes('$25,000+') || budget.includes('Enterprise')) {
    score += 25;
    qualificationNotes.push('High budget (+25)');
  } else if (budget.includes('$5,000') || budget.includes('$10,000')) {
    score += 15;
    qualificationNotes.push('Medium budget (+15)');
  } else {
    score += 5;
    qualificationNotes.push('Lower budget (+5)');
  }

  // Timeline scoring (urgency)
  const timeline = data.timeline || data.implementation_timeline || '';
  if (timeline.includes('Immediately') || timeline.includes('Within 1 month') || timeline.includes('ASAP')) {
    score += 20;
    qualificationNotes.push('Urgent timeline (+20)');
  } else if (timeline.includes('1-3 months') || timeline.includes('Next quarter')) {
    score += 10;
    qualificationNotes.push('Medium timeline (+10)');
  } else {
    score += 5;
    qualificationNotes.push('Long timeline (+5)');
  }

  // Determine lead tier
  let leadTier = 'Cold';
  if (score >= 75) {
    leadTier = 'Hot';
  } else if (score >= 50) {
    leadTier = 'Warm';
  }

  scoredItems.push({
    json: {
      ...data,
      leadScore: score,
      leadTier: leadTier,
      qualificationNotes: qualificationNotes,
      scoredAt: new Date().toISOString()
    }
  });
}

return scoredItems;

