function extractRaw(o) {
  if (typeof o === 'string') return o;
  const m = o.message || o.messages || o;
  if (typeof m?.content === 'string') return m.content;
  if (Array.isArray(m?.content)) {
    const first = m.content[0];
    if (typeof first?.text?.value === 'string') return first.text.value;
    if (typeof first?.text === 'string') return first.text;
  }
  if (Array.isArray(m)) {
    const c = m[0]?.content;
    if (typeof c === 'string') return c;
    if (Array.isArray(c)) {
      const first = c[0];
      if (typeof first?.text?.value === 'string') return first.text.value;
      if (typeof first?.text === 'string') return first.text;
    }
  }
  return JSON.stringify(o);
}
function tryParseJSON(s) {
  if (!s || typeof s !== 'string') return null;
  try { return JSON.parse(s); } catch {}
  const start = s.indexOf('{');
  const end = s.lastIndexOf('}');
  if (start >= 0 && end > start) {
    const candidate = s.slice(start, end + 1);
    try { return JSON.parse(candidate); } catch {}
  }
  return null;
}

const raw = extractRaw($json);
const parsed = tryParseJSON(raw);

if (parsed && parsed.subject && parsed.html) {
  return [{ subject: parsed.subject, html: parsed.html, ok: true }];
} else {
  return [{ ok: false, reason: 'bad_json', raw }];
}

// Pack all incoming items into one payload for the LLM.
const cities = items.map(i => ({
  city: i.json.city,
  country: i.json.country,
  summaries: i.json.summaries
}));
return [{ cities }];

// We need to get the asked city from Pick Lat/Lon node
// Since we can't use $items() reliably, we'll store it differently

// The OpenWeatherMap response has city.name which is the API's name
// But we need the original asked city name
// Check if we have it in the input, otherwise fall back to API name
const apiCityName = $json.city?.name || 'City';
const apiCountry = $json.city?.country || '';
const tzOffset = $json.city?.timezone || 0; // seconds
const data = $json.list || [];
const units = 'metric';

// Use the API city name for now - we'll fix this in the next step
const city = apiCityName;
const country = apiCountry;

function toLocalDateStr(dt) {
  // dt is unix seconds; shift by city timezone
  const d = new Date((dt + tzOffset) * 1000);
  return d.toISOString().slice(0,10); // YYYY-MM-DD
}

// Group by day
const days = {};
for (const p of data) {
  const day = toLocalDateStr(p.dt);
  if (!days[day]) days[day] = { points: [] };
  days[day].points.push(p);
}

// Aggregate per day
const daySummaries = Object.entries(days).map(([date, obj]) => {
  const pts = obj.points;

  const temps = pts.map(p => p.main.temp);
  const min = Math.min(...temps);
  const max = Math.max(...temps);

  // wind
  const windMax = Math.max(...pts.map(p => (p.wind?.gust ?? p.wind?.speed ?? 0)));

  // rain total (mm)
  const rain = pts.reduce((acc, p) => acc + (p.rain?.['3h'] || 0), 0);

  // pop average (precip probability 0..1)
  const popAvg = pts.reduce((a,p)=>a+(p.pop||0),0) / (pts.length || 1);

  // humidity average
  const humAvg = pts.reduce((a,p)=>a+(p.main?.humidity||0),0) / (pts.length || 1);

  // dominant condition by frequency
  const counts = {};
  for (const p of pts) {
    const desc = (p.weather?.[0]?.main || 'Other');
    counts[desc] = (counts[desc] || 0) + 1;
  }
  const dominant = Object.entries(counts).sort((a,b)=>b[1]-a[1])[0]?.[0] || 'Other';

  return {
    date,
    min_temp: Number(min.toFixed(1)),
    max_temp: Number(max.toFixed(1)),
    dominant_condition: dominant,
    rain_mm: Number(rain.toFixed(1)),
    precip_prob_avg: Number((popAvg*100).toFixed(0)),
    wind_max: Number(windMax.toFixed(1)),
    humidity_avg: Number(humAvg.toFixed(0)),
    unit_temp: units === 'metric' ? '¬∞C' : '¬∞F',
    unit_wind: units === 'metric' ? 'm/s' : 'mph'
  };
}).sort((a,b)=>a.date.localeCompare(b.date));

// keep only next 5 days
const next5 = daySummaries.slice(0,5);

return {
  city,
  country,
  timezone_offset_seconds: tzOffset,
  summaries: next5
};

const asked = $json.city || null;
const location = $json.body?.[0] || $json.body;

if (!location || !location.lat || !location.lon) {
  throw new Error('No valid geocoding results for: ' + asked);
}

return {
  asked_city: asked,
  city_from_api: location.name || asked,
  country: location.country || null,
  lat: location.lat,
  lon: location.lon
};

// Collect only city names from the form payload
// Filter out metadata fields like submittedAt and formMode
const cities = [];

for (const [key, value] of Object.entries($json)) {
  // Exclude known metadata fields
  if (key === 'submittedAt' || key === 'formMode') {
    continue;
  }

  // Include all other string values that are not empty
  if (typeof value === 'string' && value.trim()) {
    cities.push(value.trim());
  }
}

// Limit to 3 cities and create one item per city
return cities.slice(0, 3).map(c => ({ city: c }));
