// Get the original trigger data from the first item
const triggerData = $('Slack Trigger').first().json;

// Get thread messages from Slack API response
const slackResponse = $input.first().json;
const messagesList = slackResponse.messages || [];

// Build thread context from all messages
let threadContext = '';
if (messagesList.length > 1) {
  threadContext = messagesList
    .map((msg, idx) => {
      const text = msg.text || '';
      const user = msg.user || 'Unknown';
      const isBot = msg.bot_id ? ' (bot)' : '';
      return `Message ${idx + 1} from ${user}${isBot}: ${text}`;
    })
    .join('\n');
}

// Get current message text
const currentText = triggerData.text || (triggerData.blocks && triggerData.blocks[0] && triggerData.blocks[0].elements && triggerData.blocks[0].elements[0] && triggerData.blocks[0].elements[0].elements && triggerData.blocks[0].elements[0].elements[1] ? triggerData.blocks[0].elements[0].elements[1].text : '');

// Combine context with current message
const finalText = messagesList.length > 1 
  ? `Thread Context:\n${threadContext}\n\nCurrent Message: ${currentText}`
  : currentText;

return {
  channel: triggerData.channel || '',
  ts: triggerData.ts || '',
  thread_ts: triggerData.thread_ts || '',
  type: triggerData.type || '',
  text: finalText,
  original_text: currentText,
  is_bot: triggerData.bot_id ? true : false,
  user: triggerData.user || '',
  is_thread_continuation: triggerData.thread_ts ? true : false,
  session_id: triggerData.thread_ts || triggerData.ts,
  reaction: triggerData.reaction || '',
  item_ts: triggerData.item ? triggerData.item.ts : '',
  is_emoji: triggerData.blocks && triggerData.blocks[0] && triggerData.blocks[0].elements && triggerData.blocks[0].elements[0] && triggerData.blocks[0].elements[0].elements && triggerData.blocks[0].elements[0].elements[0] && triggerData.blocks[0].elements[0].elements[0].type === 'emoji' ? true : false,
  has_thread_context: messagesList.length > 1
};
