// === INPUT ===
// Expecting keywords already provided by the previous node (like Chat Trigger)
const inputData = $input.item.json;
const query = inputData.keywords || ""; // make sure this field exists
const keywords = Array.isArray(query)
  ? query.map(k => k.toLowerCase())
  : query.toLowerCase().split(/\s+/).filter(k => k.length > 1);

// === PROCESS EACH ITEM ===
return items.map(item => {
  const title = (item.json.title || "").toLowerCase();
  const description = (item.json.description || "").toLowerCase();

  // Combine both fields for scoring
  const text = title + " " + description;

  // Score relevance
  let score = 0;
  keywords.forEach(word => {
    const regex = new RegExp("\\b" + word + "\\b", "gi");
    const matches = text.match(regex);
    if (matches) score += matches.length * 20; // weight per keyword match
  });

  // Add extra weight if keyword appears in title
  keywords.forEach(word => {
    const regexTitle = new RegExp("\\b" + word + "\\b", "gi");
    const matchesInTitle = title.match(regexTitle);
    if (matchesInTitle) score += matchesInTitle.length * 30;
  });

  // Normalize score
  if (score > 100) score = 100;

  // Attach score
  item.json.score = score;

  return item;
});
