// Parse AI response and structure data
const aiResponse = $input.first().json.message.content;
let evaluation;

try {
  // Extract JSON from AI response
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  evaluation = JSON.parse(jsonMatch[0]);
} catch (error) {
  // Fallback if JSON parsing fails
  evaluation = {
    score: 50,
    recommendation: "Needs Review",
    strengths: ["Unable to parse"],
    weaknesses: ["AI response format error"],
    summary: aiResponse
  };
}

// Get original application data
const applicationData = $('Webhook - Receive Application').first().json.body;

// Combine all data
return {
  json: {
    candidate: {
      name: applicationData.name,
      email: applicationData.email,
      phone: applicationData.phone,
      experience: applicationData.experience,
      skills: applicationData.skills
    },
    evaluation: {
      score: evaluation.score,
      recommendation: evaluation.recommendation,
      strengths: evaluation.strengths,
      weaknesses: evaluation.weaknesses,
      summary: evaluation.summary,
      evaluatedAt: new Date().toISOString()
    },
    decision: evaluation.score >= 70 ? "Interview" : "Reject"
  }
};
