// Extract merged inputs
const newsData = items[0].json.articles || [];
const marketData = items.slice(1).map(item => item.json);

// Prepare clean dataset
const formatted = marketData.map(market => {
  // Filter relevant articles (mentioning BTC, ETH, SOL)
  const symbol = market.symbol.toUpperCase();
  const relatedArticles = newsData
    .filter(a =>
      a.title?.toLowerCase().includes(symbol.toLowerCase()) ||
      a.description?.toLowerCase().includes(symbol.toLowerCase())
    )
    .slice(0, 5) // limit to 5 for AI token efficiency
    .map(a => ({
      title: a.title,
      description: a.description,
      source: a.source?.name,
      publishedAt: a.publishedAt,
      url: a.url
    }));

  return {
    symbol,
    market_overview: {
      current_price: market.current_price,
      price_change_24h: market.price_change_24h,
      price_change_percentage_24h: market.price_change_percentage_24h,
      volume_24h: market.total_volume,
      market_cap: market.market_cap,
      high_24h: market.high_24h,
      low_24h: market.low_24h
    },
    related_news: relatedArticles
  };
});

// Output one item per crypto asset
return formatted.map(f => ({ json: f }));

