parameters.functionCode:
// üß† AI Output Parser for n8n
// Handles multiple outputs with ```json ... ``` wrappers

const parsedReports = [];

for (const item of items) {
  const raw = item.json.output;
  if (!raw || typeof raw !== "string") continue;

  // Extract JSON inside ```json ... ```
  const match = raw.match(/```json([\s\S]*?)```/);
  if (!match) continue;

  try {
    const parsed = JSON.parse(match[1].trim());

    // Filter out placeholder reports (no real data)
    if (
      parsed.market_summary &&
      !parsed.market_summary.toLowerCase().includes("not provided") &&
      !parsed.market_summary.toLowerCase().includes("no market data")
    ) {
      parsedReports.push(parsed);
    }
  } catch (err) {
    console.error("‚ùå Failed to parse AI output:", err.message);
  }
}

// If no valid reports were parsed
if (parsedReports.length === 0) {
  return [
    {
      json: {
        message: "‚ö†Ô∏è No valid AI reports found.",
        merged_discord_message: "‚ö†Ô∏è No valid AI reports found.",
        reports: [],
        avg_confidence: 0
      }
    }
  ];
}

// Merge reports into one summary object
const mergedMessage = parsedReports
  .map(r => r.discord_message)
  .filter(Boolean)
  .join("\n\n---\n\n");

const avgConfidence = Math.round(
  parsedReports.reduce((sum, r) => sum + (Number(r.confidence_score) || 0), 0) /
  parsedReports.length
);

return [
  {
    json: {
      total_reports: parsedReports.length,
      avg_confidence: avgConfidence,
      merged_discord_message: mergedMessage,
      reports: parsedReports
    }
  }
];

