const data = items.map(i => i.json);

const merged = {
  market_overview: data.map(d => ({
    symbol: d.symbol,
    ...d.market_overview
  })),
  related_news: data.flatMap(d => 
    d.related_news.map(n => ({
      symbol: d.symbol,
      ...n
    }))
  )
};

return [{ json: merged }];
