// Extract YouTube video ID from Telegram message
const items = $input.all();
const outputItems = [];

for (const item of items) {
  const messageText = item.json.message?.text || '';

  // Regex patterns for various YouTube URL formats
  const patterns = [
    /(?:youtube\.com\/watch\?v=)([a-zA-Z0-9_-]{11})/,
    /(?:youtu\.be\/)([a-zA-Z0-9_-]{11})/,
    /(?:youtube\.com\/embed\/)([a-zA-Z0-9_-]{11})/,
    /(?:youtube\.com\/v\/)([a-zA-Z0-9_-]{11})/,
    /(?:youtube\.com\/shorts\/)([a-zA-Z0-9_-]{11})/
  ];

  let videoId = null;

  // Try each pattern until we find a match
  for (const pattern of patterns) {
    const match = messageText.match(pattern);
    if (match && match[1]) {
      videoId = match[1];
      break;
    }
  }

  if (videoId) {
    outputItems.push({
      json: {
        videoId: videoId,
        videoUrl: `https://www.youtube.com/watch?v=${videoId}`,
        originalMessage: messageText
      }
    });
  } else {
    // If no video ID found, pass through with error flag
    outputItems.push({
      json: {
        error: 'No valid YouTube URL found in message',
        originalMessage: messageText
      }
    });
  }
}

return outputItems;

// Normalize YouTube API responses and extract fields for thumbnail generation

const inputItems = $input.all();
const out = [];

for (const item of inputItems) {
  // Handle string payloads (if HTTP node returned text)
  let root = item.json ?? {};
  if (typeof root === 'string') {
    try { root = JSON.parse(root); } catch (e) { root = {}; }
  }

  // Build a uniform array of "video" records to iterate
  let videos = [];
  if (Array.isArray(root.items)) {
    videos = root.items; // videoListResponse (videos.list) or search.list
  } else if (root.kind === 'youtube#video') {
    videos = [root]; // single video object shape
  } else {
    // Fallback: maybe the current item itself is a snippet
    if (root.snippet) videos = [root];
  }

  for (const v of videos) {
    const snippet = v.snippet || {};
    // videoId can be under v.id (videos.list) or v.id.videoId (search.list)
    const videoId = (v.id && typeof v.id === 'object') ? (v.id.videoId || '') : (v.id || '');

    // Title (trim to 60 chars max)
    let title = snippet.title || '';
    if (title.length > 60) title = title.slice(0, 57) + '...';

    const description = snippet.description || '';
    const channelTitle = snippet.channelTitle || '';
    const publishedAt = snippet.publishedAt || '';

    // Choose best available thumbnail
    const t = snippet.thumbnails || {};
    const thumbnailUrl =
      (t.maxres && t.maxres.url) ||
      (t.standard && t.standard.url) ||
      (t.high && t.high.url) ||
      (t.medium && t.medium.url) ||
      (t.default && t.default.url) ||
      '';

    out.push({
      json: {
        title,
        description,
        videoId,
        channelTitle,
        publishedAt,
        thumbnailUrl,
      },
    });
  }
}

// If nothing matched, still return a single empty item to avoid node errors
if (out.length === 0) {
  out.push({ json: { title: '', description: '', videoId: '', channelTitle: '', publishedAt: '', thumbnailUrl: '' } });
}

return out;

