// Get attendee data from previous node
const data = $input.first().json;

// Create QR code URL that can be used in HTML
const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=300x300&data=${encodeURIComponent(data.attendeeId)}`;

// Return all data with QR URL
return {
  name: data.name,
  email: data.email,
  event: data.event,
  role: data.role,
  attendeeId: data.attendeeId,
  initials: data.initials,
  eventDate: data.eventDate,
  timestamp: data.timestamp,
  qrCodeUrl: qrCodeUrl
};

// Get input data from webhook body
const webhookData = $input.item.json.body;

// Validation function
function validateEmail(email) {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

// Extract and validate data
const name = webhookData.name?.trim() || '';
const email = webhookData.email?.trim().toLowerCase() || '';
const eventTitle = webhookData.event || 'TechCon 2025';
const role = webhookData.role || 'Attendee';

// Check for required fields
if (!name) {
  throw new Error('Name is required');
}

if (!email || !validateEmail(email)) {
  throw new Error('Valid email is required');
}

// Generate unique attendee ID
const attendeeId = 'ATD-' + Date.now().toString(36).toUpperCase() + '-' + Math.random().toString(36).substr(2, 4).toUpperCase();

// Capitalize name properly
const capitalizedName = name
  .toLowerCase()
  .split(' ')
  .map(word => word.charAt(0).toUpperCase() + word.slice(1))
  .join(' ');

// Get initials for badge
const initials = capitalizedName
  .split(' ')
  .map(word => word[0])
  .join('')
  .toUpperCase()
  .substr(0, 2);

// Current date for event
const eventDate = 'October 25-27, 2025';

// Return sanitized data
return {
  name: capitalizedName,
  email: email,
  event: eventTitle,
  role: role,
  attendeeId: attendeeId,
  initials: initials,
  eventDate: eventDate,
  timestamp: new Date().toISOString()
};
