// Verifica se `chatInput` existe no JSON
if ($json.chatInput) {
  return [{ json: { result: $json.chatInput } }];
} 
// Caso contrário, verifica se `message.content` existe no JSON
else if ($json.message && $json.message.content) {
  return [{ json: { result: $json.message.content } }];
}
// Caso nenhum dos dois existam, retorna um valor padrão (opcional)
else {
  return [{ json: { result: 'Nenhuma entrada encontrada' } }];
}

// Acessa o array de URLs do JSON
const urls = items[0].json.urlset.url;

// Extrai os slugs das URLs
const slugs = urls.map(url => {
  const urlString = url.loc; // Pega o valor da tag <loc>
  const slug = urlString.split('/').filter(Boolean).pop(); // Extrai a última parte da URL
  return slug;
});

// Retorna os slugs de forma correta, dentro do formato esperado pelo n8n
return [
  {
    json: {
      slugs: slugs // Aqui a chave 'slugs' é um array de slugs extraídos
    }
  }
];

