// Calculate the date range (7 days ago to now)
const now = new Date();
const sevenDaysAgo = new Date(now.getTime() - (7 * 24 * 60 * 60 * 1000));

// Group executions by workflow
const workflowStats = {};

for (const item of $input.all()) {
  const workflowId = item.json.workflowId;
  const workflowName = item.json.name;
  const status = item.json.status;
  const startedAt = new Date(item.json.startedAt);
  const stoppedAt = item.json.stoppedAt ? new Date(item.json.stoppedAt) : null;

  // Calculate runtime in seconds
  let runtime = 0;
  if (stoppedAt) {
    runtime = (stoppedAt - startedAt) / 1000; // Convert to seconds
  }

  // Initialize workflow stats if not exists
  if (!workflowStats[workflowId]) {
    workflowStats[workflowId] = {
      name: workflowName,
      id: workflowId,
      error: { count: 0, totalRuntime: 0 },
      success: { count: 0, totalRuntime: 0 },
      waiting: { count: 0, totalRuntime: 0 }
    };
  }

  // Update counts and runtime based on status
  if (status === 'error') {
    workflowStats[workflowId].error.count++;
    workflowStats[workflowId].error.totalRuntime += runtime;
  } else if (status === 'success') {
    workflowStats[workflowId].success.count++;
    workflowStats[workflowId].success.totalRuntime += runtime;
  } else if (status === 'waiting') {
    workflowStats[workflowId].waiting.count++;
    workflowStats[workflowId].waiting.totalRuntime += runtime;
  }
}

// Helper function to format date (without time)
function formatDate(date) {
  const options = { 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric'
  };
  return date.toLocaleDateString('en-US', options);
}

// Helper function to format runtime
function formatRuntime(seconds) {
  if (seconds < 60) {
    return `${seconds.toFixed(2)}s`;
  } else if (seconds < 3600) {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = (seconds % 60).toFixed(0);
    return `${minutes}m ${remainingSeconds}s`;
  } else {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    return `${hours}h ${minutes}m`;
  }
}

