const inputData = $input.item.json;


const dealData = inputData.deal_details;
const personData = inputData.person_details;
const orgData = inputData.org_details;
// ÂæûËÅöÂêàÂæåÁöÑÁâ©‰ª∂‰∏≠ÊèêÂèñÈô£Âàó
const dealProducts = inputData.deal_products?.data || [];
const fieldMappingsArray = inputData.field_mappings?.data || [];


if (!dealData) {
    throw new Error("Deal data (deal_details) is missing from the input. Check the Merge node and its inputs.");
}


const fieldMapping = {};
for (const field of fieldMappingsArray) {
    if (field.key && field.name) {
        fieldMapping[field.key] = field.name;

        if ((field.field_type === 'enum' || field.field_type === 'set') && field.options) {
            fieldMapping[field.key + '_options'] = field.options;
        }
    }
}


const cleanedDeal = {};


for (const key in dealData) {
    if (dealData.hasOwnProperty(key)) {
        let value = dealData[key];


        if (fieldMapping[key]) {
            const fieldName = fieldMapping[key];
            const options = fieldMapping[key + '_options'];


            if (options && (typeof value === 'string' || typeof value === 'number')) {
                const valueArray = String(value).split(',').map(s => s.trim());
                const labels = valueArray.map(val => {
                    const option = options.find(opt => String(opt.id) === val);
                    return option ? option.label : val; 
                });
                cleanedDeal[fieldName] = (valueArray.length > 1) ? labels : labels[0];
            } else if (value && typeof value === 'object' && value !== null && value.name !== undefined) {
                cleanedDeal[fieldName] = value.name;
            } else if (value && typeof value === 'object' && value !== null && value.value !== undefined) {
                cleanedDeal[fieldName] = value.value;
            } else {
                cleanedDeal[fieldName] = value;
            }

        } else if (key.endsWith('_id') && typeof value === 'object' && value !== null && value.name !== undefined) {
            cleanedDeal[key.replace('_id', '_name')] = value.name;

        } else {
            cleanedDeal[key] = value;
        }
    }
}


if (personData) {
    cleanedDeal.person_details = personData;
}
if (orgData) {
    cleanedDeal.org_details = orgData;
}
if (dealProducts.length > 0) {
    cleanedDeal.deal_products = dealProducts;
}


return [{
    json: cleanedDeal
}];
