// Dynamic Filter Node - Config-Driven Filtering
  const config = $('Load Config from Google Drive').first().json;

  const primaryKeywords = config.primary_keywords;
  const secondaryKeywords = config.secondary_keywords;

  const filtered = [];

  for (const item of $input.all()) {
    const text = `${item.json.title} ${item.json.contentSnippet}`.toLowerCase();

    // Must mention at least one primary keyword
    const hasPrimary = primaryKeywords.some(kw => text.includes(kw.toLowerCase()));

    // Must mention at least one secondary keyword
    const hasSecondary = secondaryKeywords.some(kw => text.includes(kw.toLowerCase()));

    if (hasPrimary && hasSecondary) {
      filtered.push({
        json: {
          ...item.json,
          config: config
        }
      });
    }
  }

  return filtered;

