// Dynamic AI Prompt Generator
  const items = $input.all();
  const results = [];

  for (const item of items) {
    // Get config from item or Google Drive HTTP Request node
    const config = item.json.config || $('Load Config from Google Drive').first().json;

    // Build scoring criteria text
    let criteriaText = '';
    for (const rule of config.scoring_criteria) {
      criteriaText += `  ‚Ä¢ ${rule.category} (${rule.score_min}-${rule.score_max}): ${rule.description}\n`;
    }

    // Generate dynamic system prompt
    const systemPrompt = `You are a ${config.ai_role}. Analyze RSS articles for breaking news urgency.

  BREAKING NEWS CRITERIA:
  ${criteriaText}
  NOT BREAKING (1-4): Routine updates, opinion pieces

  INSTRUCTIONS:
  You receive: title, contentSnippet, link, source, pubDate

  1. Analyze urgency score (1-10)
  2. If score < ${config.alert_threshold}: Set should_alert = false
  3. If score >= ${config.alert_threshold}: Set should_alert = true

  Always return ALL these fields:
  - score: [1-10 based on breaking news urgency]
  - category: [Category name matching the score range]
  - alert_topic: [use the article title]
  - alert_description: [use contentSnippet]
  - impact: [your one-sentence impact analysis]
  - source: [use source field]
  - timestamp: [use pubDate]
  - link: [use link field]
  - should_alert: [false if score < ${config.alert_threshold}, true if score >= ${config.alert_threshold}]`;

    // Add systemPrompt to item
    results.push({
      json: {
        ...item.json,
        systemPrompt: systemPrompt,
        config: config
      }
    });
  }

  return results;

// Strip HTML tags helper function
 const stripHTML = (html) => {
   if (!html) return '';
   return html.replace(/<[^>]*>/g, '').trim();
 };

 // Process all items and normalize to required fields only
 const cleanedItems = [];

 for (const item of $input.all()) {
   const json = item.json;

   // Extract source (try multiple fields with fallback chain)
   const source = json.creator || json['dc:creator'] || json.author || 'Unknown';

   // Handle empty contentSnippet - fallback to content and strip HTML
   const contentSnippet = json.contentSnippet || stripHTML(json.content) || '';

   // Use isoDate for consistency (standardized ISO 8601 format)
   const pubDate = json.isoDate || json.pubDate;

   // Build clean item with ONLY required fields
   cleanedItems.push({
     json: {
       // CRITICAL fields (order matters for readability)
       link: json.link,                    // Unique identifier
       title: json.title,                  // Article headline
       contentSnippet: contentSnippet,     // Clean summary text
       pubDate: pubDate,                   // Timestamp
       source: source                      // Source attribution
     }
   });
 }

 return cleanedItems;

