{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "KeywordTopicAnalysis",
  "type": "object",
  "properties": {
    "query": {
      "type": "string",
      "description": "The original search query or keyword used in the SERP analysis."
    },
    "primary_keywords": {
      "type": "array",
      "description": "List of primary focus keywords extracted from the analyzed content or SERP data.",
      "items": { "type": "string" }
    },
    "secondary_keywords": {
      "type": "array",
      "description": "Related or supporting keywords derived from semantic clustering or co-occurrence.",
      "items": { "type": "string" }
    },
    "keyword_metrics": {
      "type": "array",
      "description": "SEO metrics for each keyword, including difficulty and search volume.",
      "items": {
        "type": "object",
        "properties": {
          "keyword": { "type": "string" },
          "search_volume": { "type": "integer" },
          "competition_score": { "type": "number" },
          "difficulty_score": { "type": "number" },
          "trend": { "type": "string", "enum": ["rising", "falling", "steady"] }
        },
        "required": ["keyword"]
      }
    },
    "focus_topics": {
      "type": "array",
      "description": "List of main content themes or topics derived from the SERP results or web pages.",
      "items": { "type": "string" }
    },
    "topic_clusters": {
      "type": "array",
      "description": "Groups of semantically related keywords and topics.",
      "items": {
        "type": "object",
        "properties": {
          "cluster_name": { "type": "string" },
          "related_keywords": { "type": "array", "items": { "type": "string" } },
          "search_intent": {
            "type": "string",
            "enum": ["informational", "navigational", "transactional", "commercial"]
          }
        },
        "required": ["cluster_name", "related_keywords"]
      }
    },
    "seo_strength_score": {
      "type": "integer",
      "description": "Overall SEO opportunity score (0–100)."
    },
    "content_gap_summary": {
      "type": "string",
      "description": "Summary describing missing topics or keyword opportunities in current content."
    },
    "competitor_insights": {
      "type": "array",
      "description": "Optional data about competitors ranking for the analyzed keyword(s).",
      "items": {
        "type": "object",
        "properties": {
          "domain": { "type": "string" },
          "ranking_position": { "type": "integer" },
          "page_title": { "type": "string" },
          "content_type": { "type": "string" },
          "keyword_overlap": { "type": "number" }
        },
        "required": ["domain", "ranking_position"]
      }
    },
    "ai_summary": {
      "type": "string",
      "description": "GPT-generated summary of the keyword and topic insights."
    },
    "timestamp": {
      "type": "string",
      "format": "date-time",
      "description": "Timestamp when the analysis was performed."
    }
  },
  "required": ["query", "primary_keywords", "focus_topics", "seo_strength_score"]
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "CompetitorInsightsAndKeywordOpportunities",
  "type": "object",
  "description": "Structured output for analyzing SERP results to extract competitor insights, keyword opportunities, and SEO metrics.",
  "properties": {
    "query": {
      "type": "string",
      "description": "The search query or topic analyzed in the SERP."
    },
    "analyzed_date": {
      "type": "string",
      "format": "date-time",
      "description": "Timestamp when the analysis was performed."
    },
    "competitors": {
      "type": "array",
      "description": "List of main competitors or domains identified in the SERP results.",
      "items": {
        "type": "object",
        "properties": {
          "domain": { "type": "string", "description": "Competitor domain or brand name." },
          "page_title": { "type": "string", "description": "Title of the competitor’s ranking page." },
          "ranking_position": { "type": "integer", "description": "SERP position for this result." },
          "snippet": { "type": "string", "description": "Short description or meta snippet from the SERP." },
          "content_type": {
            "type": "string",
            "enum": ["blog", "product_page", "landing_page", "news", "other"],
            "description": "Type of content represented by the competitor page."
          },
          "estimated_traffic_share": {
            "type": "number",
            "minimum": 0,
            "maximum": 100,
            "description": "Estimated share of search traffic (%) for this result."
          },
          "keyword_overlap": {
            "type": "number",
            "minimum": 0,
            "maximum": 1,
            "description": "Proportion of shared keywords with the target domain (0–1 scale)."
          },
          "strengths": {
            "type": "array",
            "items": { "type": "string" },
            "description": "SEO strengths or advantages identified for this competitor."
          },
          "weaknesses": {
            "type": "array",
            "items": { "type": "string" },
            "description": "Content or optimization weaknesses detected."
          }
        },
        "required": ["domain", "ranking_position"]
      }
    },
    "keyword_opportunities": {
      "type": "array",
      "description": "List of new or underutilized keywords identified from the SERP analysis.",
      "items": {
        "type": "object",
        "properties": {
          "keyword": { "type": "string" },
          "search_volume": { "type": "integer", "description": "Estimated monthly search volume." },
          "competition_score": { "type": "number", "description": "Keyword competition (0–1 scale)." },
          "difficulty_score": { "type": "number", "description": "Keyword difficulty (0–100 scale)." },
          "intent": {
            "type": "string",
            "enum": ["informational", "navigational", "transactional", "commercial"],
            "description": "User search intent."
          },
          "relevance_score": {
            "type": "number",
            "minimum": 0,
            "maximum": 1,
            "description": "Relevance to the analyzed query (0–1 scale)."
          }
        },
        "required": ["keyword"]
      }
    },
    "topic_clusters": {
      "type": "array",
      "description": "Clusters of semantically related topics derived from the SERP.",
      "items": {
        "type": "object",
        "properties": {
          "cluster_name": { "type": "string" },
          "related_keywords": { "type": "array", "items": { "type": "string" } },
          "dominant_competitor": { "type": "string", "description": "Competitor leading in this topic cluster." }
        },
        "required": ["cluster_name", "related_keywords"]
      }
    },
    "content_gap_analysis": {
      "type": "object",
      "description": "Summary of missing or underrepresented topics in the current content compared to competitors.",
      "properties": {
        "gaps_identified": { "type": "array", "items": { "type": "string" } },
        "recommendations": { "type": "array", "items": { "type": "string" } }
      }
    },
    "seo_strength_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100,
      "description": "Overall SEO opportunity score (0–100) based on keyword difficulty, gap size, and competitor weaknesses."
    },
    "ai_summary": {
      "type": "string",
      "description": "GPT-generated executive summary of the key findings, keyword opportunities, and competitor insights."
    }
  },
  "required": ["query", "competitors", "keyword_opportunities", "seo_strength_score", "ai_summary"]
}
