const items = $input.all();
const now = new Date();
const oneHourAgo = new Date(now - 60 * 60 * 1000);

// Filter recent articles and extract keywords
const filtered = items
  .filter(item => {
    const pubDate = new Date(item.json.pubDate || item.json.isoDate);
    return pubDate > oneHourAgo;
  })
  .map(item => ({
    json: {
      title: item.json.title,
      link: item.json.link,
      pubDate: item.json.pubDate || item.json.isoDate,
      content: item.json.contentSnippet || item.json.content || '',
      source: item.json.link.includes('bloomberg') ? 'Bloomberg' : 
              item.json.link.includes('cnbc') ? 'CNBC' : 'Reuters',

      // Extract potential stock tickers and keywords
      tickers: extractTickers(item.json.title + ' ' + (item.json.contentSnippet || '')),
      keywords: extractKeywords(item.json.title + ' ' + (item.json.contentSnippet || ''))
    }
  }));

function extractTickers(text) {
  const tickerPattern = /\b[A-Z]{1,5}\b/g;
  const matches = text.match(tickerPattern) || [];
  return [...new Set(matches)].filter(t => t.length >= 2).slice(0, 5);
}

function extractKeywords(text) {
  const keywords = ['earnings', 'acquisition', 'merger', 'bankruptcy', 'profit', 'loss', 
                    'revenue', 'growth', 'decline', 'surge', 'plunge', 'rally', 'crash',
                    'innovation', 'AI', 'crypto', 'Fed', 'interest', 'inflation'];
  return keywords.filter(k => text.toLowerCase().includes(k.toLowerCase()));
}

return filtered;

