// Fetch stock data from Yahoo Finance API
const ticker = $input.first().json.tickers[0] || 'SPY';
const url = `https://query1.finance.yahoo.com/v8/finance/chart/${ticker}?interval=1d&range=5d`;

try {
  const response = await fetch(url, {
    method: 'GET',
    headers: {
      'User-Agent': 'Mozilla/5.0'
    }
  });

  if (!response.ok) {
    // If API fails, return mock data to keep workflow running
    return {
      json: {
        chart: {
          result: [{
            indicators: {
              quote: [{
                close: [100, 101, 102, 103, 104],
                volume: [1000000, 1100000, 1050000, 1200000, 1150000]
              }]
            }
          }]
        }
      }
    };
  }

  const data = await response.json();
  return { json: data };

} catch (error) {
  // Fallback with mock data if fetch fails
  return {
    json: {
      chart: {
        result: [{
          indicators: {
            quote: [{
              close: [100, 101, 102, 103, 104],
              volume: [1000000, 1100000, 1050000, 1200000, 1150000]
            }]
          }
        }]
      },
      error: error.message
    }
  };
}

const newsData = $('Filter & Extract Signals').first().json;
const aiAnalysis = JSON.parse($('AI Sentiment Analysis').first().json.choices[0].message.content);
const stockData = $input.first().json;

// Calculate technical indicators
const prices = stockData?.chart?.result?.[0]?.indicators?.quote?.[0]?.close || [];
const volumes = stockData?.chart?.result?.[0]?.indicators?.quote?.[0]?.volume || [];

const latestPrice = prices[prices.length - 1];
const previousPrice = prices[prices.length - 2];
const priceChange = latestPrice && previousPrice ? ((latestPrice - previousPrice) / previousPrice * 100).toFixed(2) : 0;

// Simple Moving Average
const sma5 = prices.slice(-5).reduce((a, b) => a + b, 0) / 5;
const trend = latestPrice > sma5 ? 'Uptrend' : 'Downtrend';

// Volume analysis
const avgVolume = volumes.reduce((a, b) => a + b, 0) / volumes.length;
const latestVolume = volumes[volumes.length - 1];
const volumeRatio = (latestVolume / avgVolume).toFixed(2);

return {
  json: {
    timestamp: new Date().toISOString(),

    // News Data
    headline: newsData.title,
    source: newsData.source,
    link: newsData.link,
    tickers: newsData.tickers,
    keywords: newsData.keywords,

    // AI Analysis
    sentiment: aiAnalysis.market_sentiment || aiAnalysis.sentiment,
    impact: aiAnalysis.impact_level || aiAnalysis.impact,
    sectors: aiAnalysis.affected_sectors || [],
    signal: aiAnalysis.trading_signal || aiAnalysis.signal,
    confidence: aiAnalysis.confidence_score || 0,
    catalyst: aiAnalysis.key_catalyst || '',
    risks: aiAnalysis.risk_factors || '',

    // Technical Data
    ticker: newsData.tickers[0] || 'N/A',
    current_price: latestPrice,
    price_change_pct: priceChange,
    trend: trend,
    sma_5d: sma5.toFixed(2),
    volume_ratio: volumeRatio,

    // Composite Score
    composite_score: calculateCompositeScore(aiAnalysis, priceChange, volumeRatio),
    actionable: (aiAnalysis.confidence_score > 70 && Math.abs(priceChange) > 2) || 
                aiAnalysis.impact_level === 'Critical'
  }
};

function calculateCompositeScore(ai, priceChange, volRatio) {
  const signalScores = {
    'Strong Buy': 100, 'Buy': 75, 'Hold': 50, 'Sell': 25, 'Strong Sell': 0
  };
  const signalScore = signalScores[ai.trading_signal] || 50;
  const momentumScore = priceChange * 5; // Price change weight
  const volumeScore = volRatio > 1.5 ? 20 : 0; // Volume spike bonus
  const confidenceWeight = ai.confidence_score / 100;

  return Math.round((signalScore * confidenceWeight + momentumScore + volumeScore) * 0.85);
}

