// Extract and structure meeting data
const body = $input.first().json.body;

// Support multiple input formats
const meetingData = {
  title: body.title || body.meeting_title || body.subject || 'Untitled Meeting',
  date: body.date || body.meeting_date || new Date().toISOString(),
  attendees: body.attendees || body.participants || [],
  transcript: body.transcript || body.notes || body.content || '',
  duration: body.duration || 60,
  meeting_url: body.meeting_url || body.zoom_link || '',
  recording_url: body.recording_url || '',
  organizer: body.organizer || body.host || 'Unknown'
};

// Parse attendees if comma-separated string
if (typeof meetingData.attendees === 'string') {
  meetingData.attendees = meetingData.attendees.split(',').map(a => a.trim());
}

// Extract key metadata
meetingData.word_count = meetingData.transcript.split(/\s+/).length;
meetingData.has_recording = !!meetingData.recording_url;
meetingData.attendee_count = meetingData.attendees.length;

return {
  json: meetingData
};

