const meetingData = $('Parse Meeting Input').first().json;
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

// Calculate priority scores
function calculatePriorityScore(actionItem) {
  const priorityScores = { 'High': 3, 'Medium': 2, 'Low': 1 };
  const hasOwner = actionItem.owner && actionItem.owner !== 'not specified' ? 1 : 0;
  const hasDueDate = actionItem.due_date && actionItem.due_date !== 'not specified' ? 1 : 0;
  return (priorityScores[actionItem.priority] || 2) * 10 + hasOwner * 5 + hasDueDate * 5;
}

// Enrich action items
const enrichedActionItems = aiResponse.action_items.map((item, index) => ({
  id: `${Date.now()}-${index}`,
  task: item.task,
  owner: item.owner || 'Unassigned',
  due_date: item.due_date !== 'not specified' ? item.due_date : '',
  priority: item.priority,
  priority_score: calculatePriorityScore(item),
  status: 'Not Started',
  meeting_title: meetingData.title,
  meeting_date: meetingData.date,
  created_at: new Date().toISOString()
}));

// Sort by priority score
enrichedActionItems.sort((a, b) => b.priority_score - a.priority_score);

return {
  json: {
    // Meeting metadata
    meeting_id: `meeting-${Date.now()}`,
    meeting_title: meetingData.title,
    meeting_date: meetingData.date,
    attendees: meetingData.attendees,
    attendee_count: meetingData.attendee_count,
    duration: meetingData.duration,
    organizer: meetingData.organizer,
    meeting_url: meetingData.meeting_url,
    recording_url: meetingData.recording_url,

    // AI Analysis
    executive_summary: aiResponse.executive_summary,
    key_decisions: aiResponse.key_decisions,
    action_items: enrichedActionItems,
    action_item_count: enrichedActionItems.length,
    high_priority_count: enrichedActionItems.filter(a => a.priority === 'High').length,
    discussion_topics: aiResponse.discussion_topics,
    open_questions: aiResponse.open_questions,
    next_steps: aiResponse.next_steps,
    risks_identified: aiResponse.risks_identified,
    follow_up_meeting_needed: aiResponse.follow_up_meeting_needed,
    sentiment: aiResponse.sentiment,
    engagement_level: aiResponse.engagement_level,

    // Calculated flags
    requires_urgent_attention: enrichedActionItems.some(a => a.priority === 'High'),
    has_unassigned_tasks: enrichedActionItems.some(a => a.owner === 'Unassigned'),
    completeness_score: calculateCompletenessScore(enrichedActionItems, aiResponse)
  }
};

function calculateCompletenessScore(actionItems, analysis) {
  let score = 50; // Base score

  // Penalties
  if (actionItems.length === 0) score -= 20;
  if (actionItems.filter(a => a.owner === 'Unassigned').length > 0) score -= 10;
  if (actionItems.filter(a => !a.due_date).length > 0) score -= 10;
  if (analysis.open_questions.length > 3) score -= 10;

  // Bonuses
  if (analysis.key_decisions.length > 0) score += 15;
  if (analysis.next_steps.length > 0) score += 10;
  if (actionItems.every(a => a.owner !== 'Unassigned')) score += 15;
  if (actionItems.every(a => a.due_date)) score += 10;

  return Math.max(0, Math.min(100, score));
}

