// === „Éë„É©„É°„Éº„ÇøË®≠ÂÆö ===
const N = 20;   // ÁßªÂãïÂπ≥Âùá„ÅÆÊúüÈñì
const k = 2;    // œÉ„ÅÆÂÄçÁéáÔºà¬±2œÉÔºâ

// === „Éá„Éº„ÇøÂèñÂæó ===
let data = [];

// Marketstack„ÅÆÂá∫ÂäõÊßãÈÄ†„Çí„ÉÅ„Çß„ÉÉ„ÇØ
if (items[0].json.data) {
  // ÈÄöÂ∏∏„Ç±„Éº„ÇπÔºàMarketstack„Éé„Éº„Éâ„Åã„Çâ„Åù„ÅÆ„Åæ„ÅæÔºâ
  data = items[0].json.data;
} else {
  // ‰∫àÂÇô„Éë„Çø„Éº„É≥ÔºöitemsÈÖçÂàó„Å´„Éá„Éº„Çø„ÅåÁõ¥Êé•‰∏¶„Çì„Åß„ÅÑ„Çã„Ç±„Éº„Çπ
  data = items.map(item => item.json);
}

// N‰ª∂„Å†„ÅëÊäΩÂá∫
data = data.slice(0, N);

// === ÁµÇÂÄ§„ÅÆ„ÅøÊäΩÂá∫ ===
const closes = data.map(d => d.close).filter(v => typeof v === 'number');

// „Éá„Éº„Çø„Åå„Å™„ÅÑÂ†¥Âêà„ÅÆÂØæÁ≠ñ
if (closes.length === 0) {
  return [{ json: { error: "No valid close data found." } }];
}

// === ÁßªÂãïÂπ≥Âùá„Å®Ê®ôÊ∫ñÂÅèÂ∑Æ„ÇíË®àÁÆó ===
const mean = closes.reduce((a, b) => a + b, 0) / closes.length;
const variance = closes.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / closes.length;
const sigma = Math.sqrt(variance);

// === ÊúÄÊñ∞ÂÄ§ÔºàÂÖàÈ†≠Ôºâ ===
const latestClose = closes[0] + 100;

// === Âà§ÂÆö ===
let status = "normal";
if (latestClose > mean + k * sigma) {
  status = "high (above +2œÉ)";
} else if (latestClose < mean - k * sigma) {
  status = "low (below -2œÉ)";
}

// === Âá∫Âäõ ===
return [
  {
    json: {
      mean: mean.toFixed(2),
      sigma: sigma.toFixed(2),
      upper: (mean + k * sigma).toFixed(2),
      lower: (mean - k * sigma).toFixed(2),
      latest: latestClose.toFixed(2),
      status,
      count: closes.length
    }
  }
];

// === Hacker News Âá∫Âäõ„Åã„Çâ„Çø„Ç§„Éà„É´„ÉªÊú¨Êñá„ÉªURL„ÇíÊï¥ÂΩ¢ ===
const results = items.map(item => {
  const highlight = item.json._highlightResult || {};

  const title =
    highlight.title?.value ||
    item.json.title ||
    "No Title";

  const story =
    highlight.story_text?.value ||
    item.json.story_text ||
    "No summary available.";

  const url =
    item.json.url ||
    highlight.url?.value ||
    "No URL";

  // === HTML„Çø„Ç∞Ôºà<em>„Å™„Å©Ôºâ„ÇíÈô§Âéª ===
  const cleanTitle = title.replace(/<[^>]*>/g, "");
  const cleanStory = story.replace(/<[^>]*>/g, "");

  return `üì∞ ${cleanTitle}\n${cleanStory}\nüîó ${url}`;
});

// === Âá∫Âäõ ===
return [
  {
    json: {
      message: results.slice(0, 3).join("\n\n---\n\n"), // ‰∏ä‰Ωç3‰ª∂„ÇíÂå∫Âàá„Å£„Å¶ÁµêÂêà
    },
  },
];

const map = {
  AMZN: 'Amazon',
  AAPL: 'Apple',
  GOOG: 'Google',
  MSFT: 'Microsoft',
  TSLA: 'Tesla',
};

// ÂÖ•Âäõ„ÅÆ‰∏≠Ë∫´„ÇíÁ¢∫Ë™ç
console.log('INPUT items:', items);

const first = (items && items[0] && items[0].json) ? items[0].json : {};
const symbol = String(first.symbol || first.ticker || '').toUpperCase();

if (!symbol) {
  // symbol„ÅåÁÑ°„ÅÑÊôÇ„ÅØÁêÜÁî±„Å®ÂÖ•Âäõ„ÇíËøî„Åó„Å¶ÂèØË¶ñÂåñ
  return [
    {
      json: {
        error: 'symbol is missing',
        debugInput: first,
      },
    },
  ];
}

const keyword = map[symbol] || symbol;

return [
  {
    json: { keyword, symbol },
  },
];
