// Extract and format RSS feed data for article generation
const items = $input.all();

if (items.length === 0) {
  throw new Error('No articles found in RSS feed');
}

// Process latest article (modify to process multiple articles if needed)
const rssData = items[0].json;

// Extract topic from RSS title
const topic = rssData.title || rssData.headline || 'Untitled';

// Get article description/summary
const description = rssData.content || rssData.description || rssData.summary || '';

// Extract keywords from title (simple word extraction)
const keywords = topic
  .replace(/[„Äå„Äç„Äé„ÄèÔºàÔºâ()„ÄÅ„ÄÇÔºÅÔºü]/g, ' ')
  .split(/\s+/)
  .filter(word => word.length > 2)
  .slice(0, 5)
  .join(', ');

return [{
  json: {
    topic: topic,
    keywords: keywords || 'Technology, News',
    target_length: '1500-2000 words',
    tone: 'Professional and accessible',
    keywords_array: keywords.split(',').map(k => k.trim()).filter(k => k),
    original_url: rssData.link || rssData.url || '',
    original_title: topic,
    original_description: description.substring(0, 200),
    pub_date: rssData.pubDate || new Date().toISOString()
  }
}];

