// Structure AI-generated article with metadata
const aiOutput = $input.item.json.output || $input.item.json.text || '';
const inputData = $input.item.json;

// Extract title (first # line)
const titleMatch = aiOutput.match(/^#\s+(.+)$/m);
const title = titleMatch ? titleMatch[1] : inputData.original_title;

// Extract meta description
const metaMatch = aiOutput.match(/---\s*\n([\s\S]+)$/);
const metaDescription = metaMatch ? metaMatch[1].trim() : '';

// Article body (excluding meta description)
const articleBody = metaDescription ? aiOutput.replace(/---\s*\n[\s\S]+$/, '').trim() : aiOutput;

return {
  json: {
    topic: inputData.topic,
    keywords: inputData.keywords,
    keywords_array: inputData.keywords_array,
    target_length: inputData.target_length,
    tone: inputData.tone,
    title: title,
    article_body: articleBody,
    meta_description: metaDescription,
    word_count: articleBody.replace(/[#\s]/g, '').length,
    original_url: inputData.original_url,
    original_title: inputData.original_title,
    pub_date: inputData.pub_date,
    created_at: new Date().toISOString()
  }
};

