// Extract and format AI comparison results for Google Sheets
const allItems = $input.all();
let issues = [];

try {
  for (const item of allItems) {
    const aiResponse = item.json;

    // Handle different output structures
    let outputData;
    if (aiResponse.output) {
      outputData = [aiResponse.output];
    } else if (Array.isArray(aiResponse)) {
      outputData = aiResponse.map(entry => entry.output || entry);
    } else {
      outputData = [aiResponse];
    }

    // Process each output entry
    for (const output of outputData) {
      if (output.issues && Array.isArray(output.issues) && output.issues.length > 0) {

        // Map issues to Google Sheets format
        const mappedIssues = output.issues.map(issue => ({
          timestamp: new Date().toISOString(),
          configKey: issue.key || 'N/A',
          faqReference: issue.faq_value || 'N/A',
          actualConfig: issue.repo_value || 'N/A',
          issueType: issue.type || 'unknown',
          severity: issue.severity || 'N/A',
          suggestion: issue.recommendation || 'N/A',
          confidence: issue.confidence ? issue.confidence.toFixed(2) : 'N/A',
          issueId: issue.id || `issue_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
          summary: output.summary || 'No summary provided'
        }));

        issues.push(...mappedIssues);
      }
    }
  }

  // Handle case when no issues found
  if (issues.length === 0) {
    issues = [{
      timestamp: new Date().toISOString(),
      configKey: 'No Issues',
      faqReference: 'N/A',
      actualConfig: 'N/A',
      issueType: 'success',
      severity: 'N/A',
      suggestion: 'All configurations match FAQ reference',
      confidence: '1.00',
      issueId: `success_${Date.now()}`,
      summary: 'âœ… No discrepancies found - configs are in sync'
    }];
  }

} catch (error) {
  // Error handling
  console.error('Error processing AI output:', error);
  issues = [{
    timestamp: new Date().toISOString(),
    configKey: 'Processing Error',
    faqReference: 'Could not parse AI response',
    actualConfig: error.message,
    issueType: 'error',
    severity: 'high',
    suggestion: `Review AI output manually. Error: ${error.message}`,
    confidence: 'N/A',
    issueId: `error_${Date.now()}`,
    summary: 'âš ï¸ Processing error occurred during validation'
  }];
}

// Return formatted data for Google Sheets
return issues.map(issue => ({ json: issue }));

