// Validate and clean FAQ data
const items = $input.all();
const config = items[0].json;
const cleanedItems = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // Skip if it's the config item or header row
  if (item.json.sheet_id || i === 0) continue;

  // Get question and answer (handle different column name formats)
  const question = item.json.Question || item.json.question || item.json.A || '';
  const answer = item.json.Answer || item.json.answer || item.json.B || '';

  // Skip empty rows
  if (!question || !answer || question.trim() === '' || answer.trim() === '') {
    continue;
  }

  // Create cleaned item
  cleanedItems.push({
    json: {
      question: question.trim(),
      answer: answer.trim(),
      row_number: i,
      run_id: config.run_id,
      sheet_id: config.sheet_id,
      batch_size: config.batch_size,
      threshold_critical: config.threshold_critical,
      threshold_warning: config.threshold_warning,
      threshold_good: config.threshold_good
    }
  });
}

if (cleanedItems.length === 0) {
  throw new Error('No valid FAQ entries found in the sheet. Please check your data format.');
}

console.log(`Validated ${cleanedItems.length} FAQ entries for processing`);
return cleanedItems;

// Parse and enrich AI evaluation results with error handling
const items = $input.all();
const enrichedItems = [];

for (const item of items) {
  try {
    // Get the AI output
    const aiOutput = item.json.output || '{}';

    // Try to parse JSON from the output
    let evaluation;
    try {
      // Handle case where output might have markdown code blocks
      const jsonMatch = aiOutput.match(/\{[\s\S]*\}/);
      const jsonString = jsonMatch ? jsonMatch[0] : aiOutput;
      evaluation = JSON.parse(jsonString);
    } catch (parseError) {
      console.error('Failed to parse AI response:', aiOutput);
      evaluation = {
        score: 0,
        explanation: 'AI response parsing failed',
        strengths: 'N/A',
        improvements: 'Error in evaluation - needs manual review'
      };
    }

    // Determine severity level
    let severity = 'good';
    let severity_emoji = '‚úÖ';

    if (evaluation.score < item.json.threshold_critical) {
      severity = 'critical';
      severity_emoji = 'üî¥';
    } else if (evaluation.score < item.json.threshold_warning) {
      severity = 'warning';
      severity_emoji = '‚ö†Ô∏è';
    } else if (evaluation.score < item.json.threshold_good) {
      severity = 'acceptable';
      severity_emoji = 'üü°';
    }

    // Create enriched item
    enrichedItems.push({
      json: {
        // Original data
        question: item.json.question,
        answer: item.json.answer,
        row_number: item.json.row_number,
        run_id: item.json.run_id,

        // Evaluation results
        score: evaluation.score || 0,
        explanation: evaluation.explanation || 'No explanation provided',
        strengths: evaluation.strengths || 'N/A',
        improvements: evaluation.improvements || 'N/A',

        // Metadata
        severity: severity,
        severity_emoji: severity_emoji,
        timestamp: new Date().toISOString(),
        evaluated_at: new Date().toLocaleString('en-US', { 
          timeZone: 'UTC',
          dateStyle: 'medium',
          timeStyle: 'short'
        }),

        // For filtering
        needs_attention: evaluation.score < item.json.threshold_warning,

        // Config passthrough
        sheet_id: item.json.sheet_id
      }
    });
  } catch (error) {
    console.error('Error processing item:', error);
    // Add error item to results
    enrichedItems.push({
      json: {
        question: item.json.question || 'Unknown',
        answer: item.json.answer || 'Unknown',
        score: 0,
        explanation: `Processing error: ${error.message}`,
        severity: 'error',
        severity_emoji: '‚ùå',
        timestamp: new Date().toISOString(),
        needs_attention: true,
        sheet_id: item.json.sheet_id,
        run_id: item.json.run_id
      }
    });
  }
}

console.log(`Processed ${enrichedItems.length} evaluations`);
return enrichedItems;

// Generate comprehensive summary report
const items = $input.all();

if (items.length === 0) {
  return [{
    json: {
      error: 'No items to summarize'
    }
  }];
}

// Calculate statistics
const scores = items.map(item => item.json.score);
const totalFaqs = scores.length;
const avgScore = (scores.reduce((a, b) => a + b, 0) / totalFaqs).toFixed(2);
const maxScore = Math.max(...scores);
const minScore = Math.min(...scores);

// Count by severity
const critical = items.filter(item => item.json.severity === 'critical').length;
const warning = items.filter(item => item.json.severity === 'warning').length;
const acceptable = items.filter(item => item.json.severity === 'acceptable').length;
const good = items.filter(item => item.json.severity === 'good').length;

// Calculate pass rate (score >= 7)
const passCount = scores.filter(s => s >= 7).length;
const passRate = ((passCount / totalFaqs) * 100).toFixed(1);

// Get config from first item
const config = items[0].json;

// Find top 3 worst performing FAQs
const worstFaqs = items
  .sort((a, b) => a.json.score - b.json.score)
  .slice(0, 3)
  .map(item => ({
    question: item.json.question.substring(0, 100),
    score: item.json.score,
    improvements: item.json.improvements
  }));

// Create summary
const summary = {
  json: {
    // Summary stats
    run_id: config.run_id,
    timestamp: new Date().toISOString(),
    total_faqs: totalFaqs,
    average_score: parseFloat(avgScore),
    max_score: maxScore,
    min_score: minScore,
    pass_rate: parseFloat(passRate),

    // Severity breakdown
    critical_count: critical,
    warning_count: warning,
    acceptable_count: acceptable,
    good_count: good,

    // Status
    overall_status: critical > 0 ? 'CRITICAL' : warning > 0 ? 'NEEDS_ATTENTION' : 'HEALTHY',

    // Worst performers
    worst_performers: JSON.stringify(worstFaqs),

    // For display
    summary_text: `üìä FAQ Quality Report\n\n` +
      `üìà Overall Score: ${avgScore}/10 (${passRate}% pass rate)\n` +
      `üìù Total FAQs Evaluated: ${totalFaqs}\n\n` +
      `üéØ Severity Breakdown:\n` +
      `  üî¥ Critical: ${critical}\n` +
      `  ‚ö†Ô∏è Warning: ${warning}\n` +
      `  üü° Acceptable: ${acceptable}\n` +
      `  ‚úÖ Good: ${good}\n\n` +
      `üìâ Score Range: ${minScore} - ${maxScore}`,

    // For alerts
    needs_alert: critical > 0 || warning > 0,
    sheet_id: config.sheet_id,

    // Pass through all items for next node
    all_results: items
  }
};

console.log('Summary generated:', summary.json.summary_text);
return [summary];

