const htmlString = $input.first().json.output.email_body ?? "";

function cleanHTMLString(s) {
  if (typeof s !== "string") s = String(s ?? "");
  return s
    .replace(/\r?\n/g, "")       // real newlines
    .replace(/\\[nrt]/g, "")     // escaped \n, \r, \t
    .replace(/\s{2,}/g, " ")     // collapse multiple spaces
    .replace(/\\+/g, "")         // stray backslashes
    .replace(/\u00A0/g, " ")     // non-breaking spaces
    .replace(/<!--[\s\S]*?-->/g, "") // (optional) strip HTML comments
    .trim();
}

return {
  cleaned: cleanHTMLString(htmlString),
};

