const skyscannerFlights = $input.item(0).json;
const kiwiFlights = $input.item(1).json;
const bookingHotels = $input.item(2).json;
const weather = $input.item(3).json;
const activities = $input.item(4).json;
const requestData = $('Extract Request Data').first().json;

const allFlightData = {
  skyscanner: skyscannerFlights,
  kiwi: kiwiFlights,
  weather: weather,
  activities: activities
};

const hotelData = bookingHotels;

return [{
  json: {
    flights: allFlightData,
    hotels: hotelData,
    request: requestData
  }
}];

const mergedData = $input.first().json;
const requestData = mergedData.request;

// Parse Skyscanner flights
const skyscannerFlights = [];
if (mergedData.flights.skyscanner.itineraries?.results) {
  mergedData.flights.skyscanner.itineraries.results.slice(0, 8).forEach((flight, i) => {
    skyscannerFlights.push({
      id: `sky_${i}`,
      source: 'Skyscanner',
      airline: flight.legs[0]?.carriers?.marketing[0]?.name || 'Unknown',
      departureTime: flight.legs[0]?.departure || '',
      arrivalTime: flight.legs[0]?.arrival || '',
      duration: flight.legs[0]?.duration || 0,
      stops: flight.legs[0]?.stopCount || 0,
      price: flight.pricing?.price?.amount || 0,
      currency: flight.pricing?.price?.currency || 'USD',
      bookingLink: flight.deeplink || '#'
    });
  });
}

// Parse Kiwi flights
const kiwiFlights = [];
if (mergedData.flights.kiwi.data) {
  mergedData.flights.kiwi.data.slice(0, 8).forEach((flight, i) => {
    kiwiFlights.push({
      id: `kiwi_${i}`,
      source: 'Kiwi.com',
      airline: flight.airlines?.[0] || 'Unknown',
      departureTime: new Date(flight.local_departure).toISOString(),
      arrivalTime: new Date(flight.local_arrival).toISOString(),
      duration: flight.duration?.total || 0,
      stops: (flight.route?.length || 1) - 1,
      price: flight.price || 0,
      currency: flight.currency || 'USD',
      bookingLink: flight.deep_link || '#'
    });
  });
}

const allFlights = [...skyscannerFlights, ...kiwiFlights].sort((a,b) => a.price - b.price);

// Parse hotels
const hotels = [];
if (mergedData.hotels.result) {
  mergedData.hotels.result.slice(0, 10).forEach((hotel, i) => {
    hotels.push({
      id: `hotel_${i}`,
      name: hotel.hotel_name || 'Unknown',
      address: hotel.address || '',
      rating: hotel.review_score || 0,
      reviewCount: hotel.review_nr || 0,
      price: hotel.min_total_price || 0,
      pricePerNight: hotel.price_breakdown?.gross_price || 0,
      currency: hotel.currency_code || 'USD',
      amenities: hotel.unit_configuration_label || '',
      distanceToCenter: hotel.distance || '',
      bookingLink: hotel.url || '#'
    });
  });
}

// Weather summary
let weatherSummary = 'No weather data';
if (mergedData.flights.weather?.list) {
  const temps = mergedData.flights.weather.list.slice(0, 8).map(w => w.main.temp);
  const avgTemp = (temps.reduce((a,b)=>a+b,0)/temps.length).toFixed(1);
  const conditions = mergedData.flights.weather.list[0]?.weather[0]?.description || 'N/A';
  weatherSummary = `${avgTemp}¬∞C, ${conditions}`;
}

// Activities
const activities = [];
if (mergedData.flights.activities?.data) {
  mergedData.flights.activities.data.slice(0, 10).forEach((act, i) => {
    activities.push({
      id: `act_${i}`,
      name: act.title || 'Activity',
      description: act.description || '',
      rating: act.rating || 0,
      reviewCount: act.reviewCount || 0,
      price: act.price?.amount || 0,
      currency: act.price?.currency || 'USD',
      duration: act.duration || '',
      bookingLink: act.productUrl || '#'
    });
  });
}

// Create enhanced itineraries (top 8)
const itineraries = [];
for (let f = 0; f < Math.min(4, allFlights.length); f++) {
  for (let h = 0; h < Math.min(2, hotels.length); h++) {
    if (itineraries.length >= 8) break;

    const flight = allFlights[f];
    const hotel = hotels[h];
    const selectedActivities = activities.slice(0, 3);

    const totalPrice = flight.price + hotel.price + selectedActivities.reduce((sum, act) => sum + act.price, 0);

    itineraries.push({
      id: `itinerary_${itineraries.length + 1}`,
      flight: flight,
      hotel: hotel,
      activities: selectedActivities,
      weather: weatherSummary,
      totalPrice: totalPrice,
      currency: flight.currency,
      destination: requestData.destination,
      departureCity: requestData.departureCity,
      checkInDate: requestData.checkInDate,
      checkOutDate: requestData.checkOutDate,
      travelers: requestData.travelers
    });
  }
  if (itineraries.length >= 8) break;
}

itineraries.sort((a, b) => a.totalPrice - b.totalPrice);

return itineraries.slice(0, 8).map(it => ({ json: it }));

const itineraries = $input.all().map(item => item.json);
const aiAnalysis = $('AI Agent - Itinerary Optimizer').first().json;

let aiScores = [];
try {
  aiScores = typeof aiAnalysis.output === 'string' ? JSON.parse(aiAnalysis.output) : aiAnalysis.output;
} catch (e) {
  aiScores = itineraries.map((_, i) => ({
    score: 85 - i * 5,
    reasoning: 'AI analysis pending',
    highlights: ['Competitive pricing', 'Good availability'],
    warnings: []
  }));
}

const enrichedItineraries = itineraries.map((itinerary, index) => {
  const aiData = aiScores[index] || { score: 75, reasoning: 'Standard option', highlights: [], warnings: [] };
  return {
    ...itinerary,
    aiScore: aiData.score || 75,
    aiReasoning: aiData.reasoning || 'Good option',
    aiHighlights: aiData.highlights || [],
    aiWarnings: aiData.warnings || []
  };
});

enrichedItineraries.sort((a, b) => b.aiScore - a.aiScore);

return enrichedItineraries.map(it => ({ json: it }));

const itineraries = $input.all().map(item => item.json);

if (itineraries.length === 0) {
  return [{ json: { html: '<html><body><h2>No Results</h2></body></html>' } }];
}

const first = itineraries[0];
let html = `
<!DOCTYPE html>
<html>
<head>
