  .comparison-table th { background: #667eea; color: white; padding: 15px; text-align: left; font-weight: 600; }
  .comparison-table td { padding: 12px; border-bottom: 1px solid #e0e0e0; }
  .comparison-table tr:hover { background: #f8f9fa; }
  .badge { display: inline-block; padding: 5px 12px; background: #ffd700; color: #1a1a1a; border-radius: 15px; font-size: 12px; font-weight: 700; margin-left: 10px; }
  .weather-box { background: linear-gradient(135deg, #4facfe, #00f2fe); color: white; padding: 15px; border-radius: 8px; text-align: center; margin: 15px 0; }
</style>
</head>
<body>
<div class="container">
  <div class="header">
    <h1>‚úàÔ∏è Your AI-Optimized Travel Itineraries</h1>
    <p style="margin: 10px 0 0 0; font-size: 18px; opacity: 0.9;">Powered by Advanced AI Analysis</p>
  </div>

  <div class="summary">
    <strong>üìç Route:</strong> ${first.departureCity} ‚Üí ${first.destination}<br>
    <strong>üìÖ Dates:</strong> ${first.checkInDate} to ${first.checkOutDate}<br>
    <strong>üë• Travelers:</strong> ${first.travelers}<br>
    <strong>üå§Ô∏è Weather:</strong> ${first.weather}
  </div>

  <div style="padding: 30px;">
    <h2 style="color: #667eea; font-size: 28px; margin-bottom: 20px;">üèÜ Top ${itineraries.length} AI-Ranked Itineraries</h2>
`;

itineraries.forEach((it, index) => {
  const isBest = index === 0;
  html += `
    <div class="itinerary ${isBest ? 'best-deal' : ''}">
      <div class="itinerary-header" ${isBest ? 'style="background: linear-gradient(135deg, #ffd700, #ffed4e); color: #1a1a1a;"' : ''}>
        <div>
          <h3 style="margin: 0; font-size: 24px;">Option ${index + 1} ${isBest ? '<span class="badge">BEST VALUE</span>' : ''}</h3>
          <p style="margin: 5px 0 0 0; opacity: 0.8;">AI-Optimized Package</p>
        </div>
        <div class="ai-score">ü§ñ ${it.aiScore}/100</div>
      </div>

      <div class="section">
        <div class="ai-insights">
          <strong style="color: #667eea; font-size: 16px;">üß† AI Analysis:</strong>
          <p style="margin: 10px 0;">${it.aiReasoning}</p>
          ${it.aiHighlights.length > 0 ? `<div class="highlight">‚úì ${it.aiHighlights.join(' ‚Ä¢ ')}</div>` : ''}
          ${it.aiWarnings.length > 0 ? `<div class="warning">‚ö† ${it.aiWarnings.join(' ‚Ä¢ ')}</div>` : ''}
        </div>

        <div class="section-title">‚úàÔ∏è Flight Details</div>
        <div class="detail-grid">
          <div class="detail-item"><div class="detail-label">Source</div><div class="detail-value">${it.flight.source}</div></div>
          <div class="detail-item"><div class="detail-label">Airline</div><div class="detail-value">${it.flight.airline}</div></div>
