// Extract and clean user input
const userQuestion = $input.item.json.body.question || $input.item.json.body.message || $input.item.json.body.text;
const userId = $input.item.json.body.user_id || $input.item.json.body.userId || 'anonymous';
const sessionId = $input.item.json.body.session_id || $input.item.json.body.sessionId || `session_${Date.now()}`;
const userName = $input.item.json.body.user_name || $input.item.json.body.userName || 'Traveler';

// Clean the question
const cleanedQuestion = userQuestion.trim();

return {
  json: {
    userQuestion: cleanedQuestion,
    userId: userId,
    sessionId: sessionId,
    userName: userName,
    timestamp: new Date().toISOString(),
    questionLength: cleanedQuestion.length
  }
};

// Extract category from AI response
const category = $input.item.json.choices[0].message.content.trim().toUpperCase();
const previousData = $('Extract & Clean Question').item.json;

return {
  json: {
    ...previousData,
    category: category,
    categoryDetected: true
  }
};

// Retrieve relevant knowledge base content based on category
const category = $input.item.json.category;

// Knowledge base for different categories
const knowledgeBase = {
  DESTINATIONS: `Popular destinations include: Paris (France) - Eiffel Tower, museums; Tokyo (Japan) - temples, technology; New York (USA) - landmarks, culture; Dubai (UAE) - luxury, architecture; Bali (Indonesia) - beaches, temples; London (UK) - history, culture; Rome (Italy) - ancient sites; Barcelona (Spain) - architecture, beaches; Sydney (Australia) - Opera House, beaches; Singapore - modern city, gardens. Best times to visit vary by destination. Europe: April-October. Asia: November-March. Americas: Year-round with seasonal variations.`,

  PACKAGES: `Travel packages available: Honeymoon packages (7-14 days) with romantic destinations; Family packages with kid-friendly activities; Adventure packages including trekking, diving, skiing; Beach resort packages with all-inclusive options; Cultural tour packages with guided experiences; Business travel packages with flexible bookings; Group tour packages with fixed itineraries; Custom packages tailored to preferences. Packages include flights, hotels, transfers, and selected activities. Early bird discounts up to 20% available.`,

  VISA: `Visa requirements vary by destination and passport: EU citizens can travel visa-free within Schengen area; US/UK/Canada citizens have visa-free access to 150+ countries; Tourist visas typically valid 30-90 days; Business visas require company invitation letter; E-visas available for many countries (Turkey, India, Australia, etc.); Visa-on-arrival available in Thailand, Indonesia, Maldives; Processing time: 3-15 business days; Required documents: valid passport (6 months validity), photos, travel itinerary, proof of accommodation, bank statements.`,

  TRANSPORT: `Transportation options: International flights with major airlines (economy, business, first class); Domestic flights for inter-city travel; Airport transfers (private car, shuttle, taxi, metro); Train services (high-speed, regional); Car rentals with international/local companies; Private chauffeurs available; Bike rentals in tourist cities; Ferry services for island destinations; Metro/subway in major cities; Rideshare services (Uber, Lyft) available in most cities. Pre-booking recommended for better rates.`,

  HOTELS: `Accommodation options: 5-star luxury hotels with premium amenities; 4-star business hotels with conference facilities; 3-star tourist hotels for budget travelers; Boutique hotels with unique experiences; Beach resorts with all-inclusive packages; Airport hotels for layovers; Hostels for backpackers; Vacation rentals (Airbnb, apartments); Bed & breakfasts; Eco-lodges and sustainable accommodations. Amenities include: WiFi, breakfast, pool, gym, spa, concierge, room service. Free cancellation available on selected bookings.`,

  ACTIVITIES: `Popular activities: City tours (walking, bus, bike); Museum and gallery visits; Adventure sports (diving, skiing, paragliding); Cultural experiences (cooking classes, traditional shows); Beach activities (snorkeling, surfing, sailing); Wildlife safaris and nature tours; Food and wine tours; Shopping tours in local markets; Spa and wellness experiences; Night entertainment and cruises; Photography tours; Theme parks and attractions. Activities can be pre-booked or arranged on arrival.`,

  BOOKING: `Booking process: Search flights/hotels on website or app; Select dates, passengers, preferences; Compare prices and options; Add extras (meals, baggage, insurance); Enter passenger details; Choose payment method (card, PayPal, installments); Receive instant confirmation email; E-tickets sent to email/app; Check-in online 24-48 hours before departure; Modify booking through manage booking section. Customer support available 24/7. Book in advance for better prices (2-3 months recommended).`,

  CANCELLATION: `Cancellation policy: Free cancellation up to 24 hours for most bookings; Refundable tickets allow cancellation with fees; Non-refundable tickets have restrictions; Cancellation fees: 0-100% depending on timing; Hotels: usually free cancellation 1-3 days before check-in; Packages: cancellation fees apply per component; Medical emergencies considered for exceptions; Travel insurance covers cancellations; Refund processing: 7-14 business days; Online cancellation available through manage booking. Contact support for assistance.`,

  BAGGAGE: `Baggage allowance: Economy class - 1 cabin bag (7-10kg) + 1 checked bag (20-23kg); Business class - 2 cabin bags + 2 checked bags (32kg each); Cabin baggage dimensions: 55x40x20cm; Excess baggage charged per kg; Liquid restrictions: max 100ml containers in clear bag; Sports equipment charged separately; Musical instruments as cabin baggage (subject to size); Prohibited items: weapons, explosives, flammables; Fragile items declaration recommended; Lost baggage claim at airport desk; Delayed baggage compensation available.`,

  GENERAL: `General travel information: Customer service available 24/7 via phone, email, chat; Mobile app for iOS and Android; Loyalty program with points and rewards; Travel insurance recommended for all bookings; Payment options: credit/debit cards, PayPal, installments; Multi-currency support; Newsletter for deals and offers; Best price guarantee; Flexible booking options; Group booking discounts; Corporate travel solutions; Travel advisories and safety tips; Passport and document checklist; Health and vaccination requirements; Currency exchange information.`
};

const relevantContext = knowledgeBase[category] || knowledgeBase['GENERAL'];

return {
  json: {
    ...$input.item.json,
    knowledgeContext: relevantContext,
    contextLength: relevantContext.length
  }
};

// Extract AI answer and add helpful resources
const aiAnswer = $input.item.json.choices[0].message.content.trim();
const previousData = $('Fetch Knowledge Base Context').item.json;
const category = previousData.category;

// Add relevant quick links based on category
const quickLinks = {
  DESTINATIONS: ['View All Destinations', 'Travel Guides', 'Best Time to Visit'],
  PACKAGES: ['Browse Packages', 'Custom Package Builder', 'Special Offers'],
  VISA: ['Visa Requirements Checker', 'Document Checklist', 'Visa Application'],
  TRANSPORT: ['Book Flights', 'Airport Transfers', 'Car Rentals'],
  HOTELS: ['Search Hotels', 'Hotel Deals', 'Partner Properties'],
  ACTIVITIES: ['Popular Activities', 'Book Tours', 'Local Experiences'],
  BOOKING: ['New Booking', 'Manage Booking', 'Check-in Online'],
  CANCELLATION: ['Cancel Booking', 'Refund Status', 'Change Booking'],
  BAGGAGE: ['Baggage Policy', 'Add Extra Baggage', 'Track Baggage'],
  GENERAL: ['Contact Support', 'FAQs', 'Travel Tips']
};

const relatedLinks = quickLinks[category] || quickLinks['GENERAL'];

return {
  json: {
    answer: aiAnswer,
    category: category,
    relatedLinks: relatedLinks,
    userQuestion: previousData.userQuestion,
    userName: previousData.userName,
    userId: previousData.userId,
    sessionId: previousData.sessionId,
    timestamp: previousData.timestamp,
    answerLength: aiAnswer.length,
    tokensUsed: $input.item.json.usage?.total_tokens || 0
  }
};

// Log satisfied interaction
const logData = $input.item.json;

return {
  json: {
    ...logData,
    satisfactionStatus: 'satisfied',
    followUpOffered: false,
    needsHumanSupport: false
  }
};

// Add follow-up offer for unsure users
const logData = $input.item.json;

return {
  json: {
    ...logData,
    satisfactionStatus: 'needs_followup',
    followUpOffered: true,
    followUpMessage: 'Would you like to speak with a travel specialist for more personalized assistance? Or do you have any other questions?',
    supportOptions: ['Chat with Agent', 'Schedule Call', 'Send Email', 'Continue with Bot']
  }
};
