parameters.jsCode:
const data = $input.first().json;

return [
  {
    json: {
      keyword: data.search_parameters?.q || "",
      country: data.search_parameters?.uule || "",
      total_results: data.search_information?.total_results || null,

      organic_results: (data.organic_results || []).map(r => ({
        position: r.position,
        title: r.title,
        snippet: r.snippet,
        link: r.link,
        source: r.source || null,
        date: r.date || null
      })),

      videos: (data.inline_videos || []).map(v => ({
        title: v.title,
        platform: v.platform,
        duration: v.duration,
        link: v.link
      })),

      related_searches: (data.related_searches || []).map(rs => rs.query)
    }
  }
];

