parameters.jsCode:
return [
  {
    json: {
      // Core identifiers
      keyword: $json.keyword,
      country: $json.country,

      // Fields UI is clearly expecting
      search_intent: $json.seo_summary?.search_intent || "",
      competition: $json.seo_summary?.competition_level || "",
      serp_features: $json.seo_summary?.serp_features || [],

      // Main sections
      content_gaps: $json.content_gaps || [],
      content_opportunities: $json.content_opportunities || [],
      recommended_content_types: $json.recommended_content_types || [],

      // CRITICAL: UI confidence binding
      confidence: $json.confidence_score || "Not determined"
    }
  }
];
