parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"correctionCount": {
			"type": "number",
			"description": "Total number of corrections made"
		},
		"corrections": {
			"type": "array",
			"items": {
				"type": "object",
				"properties": {
					"transactionId": {
						"type": "string",
						"description": "Unique identifier for the transaction"
					},
					"originalValue": {
						"type": "string",
						"description": "Original value before correction"
					},
					"correctedValue": {
						"type": "string",
						"description": "Corrected value"
					},
					"correctionType": {
						"type": "string",
						"description": "Type of correction applied"
					},
					"explanation": {
						"type": "string",
						"description": "Explanation for the correction"
					},
					"timestamp": {
						"type": "string",
						"description": "Timestamp when correction was made"
					}
				},
				"required": ["transactionId", "originalValue", "correctedValue", "correctionType", "explanation", "timestamp"]
			}
		}
	},
	"required": ["correctionCount", "corrections"]
}

parameters.jsCode:
// Generate Comprehensive Compliance Summary Report
const items = $input.all();

// Initialize counters and data structures
let totalTransactions = 0;
let categorizedTransactions = 0;
let anomaliesDetected = 0;
let correctionsMade = 0;
let syncedTransactions = 0;
let failedSyncs = 0;

const categoryBreakdown = {};
const anomalyTypes = [];
const correctionDetails = [];

// Process all items to gather statistics
items.forEach(item => {
  const data = item.json;

  // Count total transactions
  totalTransactions++;

  // Track categorization
  if (data.category || data.taxCategory) {
    categorizedTransactions++;
    const category = data.category || data.taxCategory;
    categoryBreakdown[category] = (categoryBreakdown[category] || 0) + 1;
  }

  // Track anomalies
  if (data.hasAnomaly || data.anomalyDetected) {
    anomaliesDetected++;
    if (data.anomalyType) {
      anomalyTypes.push(data.anomalyType);
    }
  }

  // Track corrections
  if (data.corrected || data.correctionApplied) {
    correctionsMade++;
    if (data.correctionDetails) {
      correctionDetails.push(data.correctionDetails);
    }
  }

  // Track sync status
  if (data.syncStatus === 'success' || data.synced === true) {
    syncedTransactions++;
  } else if (data.syncStatus === 'failed' || data.synced === false) {
    failedSyncs++;
  }
});

// Calculate compliance score (0-100)
let complianceScore = 100;

// Deduct points for anomalies
if (totalTransactions > 0) {
  const anomalyRate = (anomaliesDetected / totalTransactions) * 100;
  complianceScore -= anomalyRate * 0.5; // 0.5 point per 1% anomaly rate
}

// Deduct points for failed syncs
if (totalTransactions > 0) {
  const failedSyncRate = (failedSyncs / totalTransactions) * 100;
  complianceScore -= failedSyncRate * 0.3; // 0.3 point per 1% failed sync rate
}

// Bonus points for corrections made
if (anomaliesDetected > 0) {
  const correctionRate = (correctionsMade / anomaliesDetected) * 100;
  complianceScore += correctionRate * 0.1; // 0.1 point per 1% correction rate
}

// Ensure score is between 0 and 100
complianceScore = Math.max(0, Math.min(100, complianceScore));

// Determine compliance status
let complianceStatus = 'Excellent';
if (complianceScore < 95) complianceStatus = 'Good';
if (complianceScore < 85) complianceStatus = 'Fair';
if (complianceScore < 70) complianceStatus = 'Needs Attention';
if (complianceScore < 50) complianceStatus = 'Critical';

// Generate summary report
const summary = {
  reportDate: new Date().toISOString(),
  complianceScore: Math.round(complianceScore * 100) / 100,
  complianceStatus: complianceStatus,

  transactionSummary: {
    totalTransactions: totalTransactions,
    categorizedTransactions: categorizedTransactions,
    categorizationRate: totalTransactions > 0 ? Math.round((categorizedTransactions / totalTransactions) * 100) : 0
  },

  categorizationBreakdown: categoryBreakdown,

  anomalySummary: {
    anomaliesDetected: anomaliesDetected,
    anomalyRate: totalTransactions > 0 ? Math.round((anomaliesDetected / totalTransactions) * 100) : 0,
    anomalyTypes: [...new Set(anomalyTypes)]
  },

  correctionSummary: {
    correctionsMade: correctionsMade,
    correctionRate: anomaliesDetected > 0 ? Math.round((correctionsMade / anomaliesDetected) * 100) : 0,
    correctionDetails: correctionDetails
  },

  syncStatus: {
    syncedTransactions: syncedTransactions,
    failedSyncs: failedSyncs,
    syncSuccessRate: totalTransactions > 0 ? Math.round((syncedTransactions / totalTransactions) * 100) : 0
  },

  recommendations: []
};

// Add recommendations based on findings
if (anomaliesDetected > 0 && correctionsMade < anomaliesDetected) {
  summary.recommendations.push('Review and correct remaining anomalies to improve compliance score');
}

if (failedSyncs > 0) {
  summary.recommendations.push('Investigate and resolve failed synchronizations with accounting software');
}

if (complianceScore < 85) {
  summary.recommendations.push('Compliance score is below optimal level - immediate review recommended');
}

if (categorizedTransactions < totalTransactions) {
  summary.recommendations.push('Some transactions remain uncategorized - complete categorization for full compliance');
}

return [{ json: summary }];
