parameters.jsCode:
// Get data from specific nodes by name
const orders = $items("HTTP Orders");
const refunds = $items("HTTP Refunds");

const output = [];

// Create lookup map for orders
const orderMap = {};
for (const o of orders) {
  orderMap[o.json.id] = o.json;
}

// Loop refunds and attach line items
for (const r of refunds) {
  const refund = r.json;
  const order = orderMap[refund.parent_id];

  if (!order || !order.line_items?.length) continue;

  for (const li of order.line_items) {
    output.push({
      order_id: refund.parent_id,
      refund_id: refund.id,
      sku: li.sku || li.name || li.product_id,
      quantity: li.quantity,
      reason: refund.reason,
      refund_date_utc: refund.date_created_gmt
    });
  }
}

return output.map(o => ({ json: o }));

// ================= CONFIG =================
const WINDOW_HOURS = 24;

// ================= TIME WINDOWS =================
const now = new Date();
const currentStart = new Date(now.getTime() - WINDOW_HOURS * 60 * 60 * 1000);
const previousStart = new Date(currentStart.getTime() - WINDOW_HOURS * 60 * 60 * 1000);

// ================= AGGREGATION MAP =================
const stats = {}; // âœ… THIS WAS MISSING (root cause)

// ================= PROCESS RETURNS =================
for (const item of items) {
  const sku =
    item.json.sku ||
    item.json.product_id ||
    "UNKNOWN_SKU";

  const refundDateRaw = item.json.refund_date_utc;
  const reason = item.json.reason || "Unknown";

  if (!refundDateRaw) continue;

  const refundDate = new Date(refundDateRaw);
  if (isNaN(refundDate)) continue;

  if (!stats[sku]) {
    stats[sku] = {
      current: 0,
      previous: 0,
      reasons: {}
    };
  }

  // ðŸ‘‰ Count refunds (NOT quantity â€” more reliable)
  if (refundDate >= currentStart) {
    stats[sku].current += 1;
  } else if (refundDate >= previousStart && refundDate < currentStart) {
    stats[sku].previous += 1;
  }

  stats[sku].reasons[reason] =
    (stats[sku].reasons[reason] || 0) + 1;
}

// ================= BUILD OUTPUT =================
const output = [];

for (const sku in stats) {
  const current = stats[sku].current;
  const previous = stats[sku].previous;

  const absolute_increase = current - previous;

  const percent_increase =
    previous === 0
      ? (current > 0 ? null : 0) // null = no baseline
      : Math.round((absolute_increase / previous) * 100);

  output.push({
    json: {
      sku,
      current_returns: current,
      previous_returns: previous,
      absolute_increase,
      percent_increase,
      reasons: stats[sku].reasons
    }
  });
}

return output;

const now = new Date();
const WINDOW_HOURS = 24;
const ms = WINDOW_HOURS * 60 * 60 * 1000;

const currentEnd = now;
const currentStart = new Date(now.getTime() - ms);
const prevEnd = currentStart;
const prevStart = new Date(prevEnd.getTime() - ms);

function iso(d) {
  return d.toISOString().split('.')[0] + 'Z';
}

return [
  {
    json: {
      timeWindow: {
        currentStart: iso(currentStart),
        currentEnd: iso(currentEnd),
        prevStart: iso(prevStart),
        prevEnd: iso(prevEnd),
      }
    }
  }
];

const output = [];

for (const item of items) {
  const text = item.json.message?.text || "";

  // Helper function
  const getMatch = (regex) => {
    const m = text.match(regex);
    return m ? m[1].trim() : null;
  };

  // Extract basic fields
  const sku = getMatch(/SKU:\s*(.+)/);
  const increaseRaw = getMatch(/Increase:\s*([^\n]+)/);
  const currentReturns = getMatch(/Current Returns:\s*(\d+)/);
  const previousReturns = getMatch(/Previous Returns:\s*(\d+)/);
  const status = getMatch(/Status:\s*(.+)/);
  const runDateUtc = getMatch(/Run Date \(UTC\):\s*(.+)/);
  const cooldownKey = getMatch(/Cooldown Key:\s*(.+)/);

  // Percent increase (handle N/A)
  const percentIncrease =
    increaseRaw && increaseRaw !== "N/A%"
      ? parseInt(increaseRaw.replace("%", ""), 10)
      : null;

  // Extract reasons block
  const reasonsBlockMatch = text.match(/Reasons:\n([\s\S]*?)\n\nStatus:/);
  const reasonsText = reasonsBlockMatch ? reasonsBlockMatch[1].trim() : "";

  // Convert reasons into object
  const reasons = {};
  reasonsText.split("\n").forEach(line => {
    const parts = line.split(":");
    if (parts.length === 2) {
      const reason = parts[0].trim();
      const count = parseInt(parts[1].trim(), 10);
      if (!isNaN(count)) {
        reasons[reason] = count;
      }
    }
  });

  output.push({
    json: {
      sku,
      current_returns: currentReturns ? parseInt(currentReturns, 10) : null,
      previous_returns: previousReturns ? parseInt(previousReturns, 10) : null,
      percent_increase: percentIncrease,
      reasons_raw: reasonsText,               // human-readable
      reasons_json: JSON.stringify(reasons),  // machine-readable
      status,
      run_date_utc: runDateUtc,
      cooldown_key: cooldownKey
    }
  });
}

return output;
