const html = $input.first().json.data ?? "";
// Supprime les balises HTML, garde 10 000 premiers caract√®res max

 const text = html.replace(/<script[\s\S]*?<\/script>/gi, '')
   .replace(/<link[\s\S]*?<\/link>/gi, '')
   .replace(/<meta[\s\S]*?<\/meta>/gi, '')
   .replace(/<button[\s\S]*?<\/button>/gi, '')
   .replace(/<li[\s\S]*?<\/li>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
   .replace(/<footer[\s\S]*?<\/footer>/gi, '') 
    .replace(/<svg[\s\S]*?<\/svg>/gi, '').trim();

// Supprime les balises autofermantes comme <link />, <meta />, <img />, etc.
const cleantext = text.replace(/<\s*(footer|button|link|meta|img|br|hr|input|source|track|wbr|embed|param|area|col|base|keygen|command|frame|iframe|object|picture|portal|slot|data|bgsound|isindex)[^>]*\/?>/gi, '').trim();

const MAX = 12000;
const snippet = cleantext.slice(0, MAX)

return [{ json: { content: snippet } }];

const item = $input.first().json;

const first = item.items && item.items[0] ? item.items[0].link : null;

// On supprime les champs inutiles (queries, kind, etc.)
// Construit un nouvel objet en excluant "queries" et "kind"
const { queries, kind, searchInformation, context, items, ...cleaned } = item;

return {
  json: {
    ...cleaned,
    url: first,
  },
};
