// Parse the job description analysis from Claude
const item = $input.first().json;

try {
  // Get the text content from Claude response
  let responseText = '';
  if (item.content && item.content[0] && item.content[0].text) {
    responseText = item.content[0].text;
  } else {
    throw new Error('No content in Claude response');
  }

  // Clean up the response (remove any markdown formatting if present)
  responseText = responseText.trim();
  if (responseText.startsWith('```json')) {
    responseText = responseText.replace(/```json\n?/, '').replace(/\n?```$/, '');
  } else if (responseText.startsWith('```')) {
    responseText = responseText.replace(/```\n?/, '').replace(/\n?```$/, '');
  }

  // Parse the JSON
  const parsed = JSON.parse(responseText);

  // Validate required fields
  const required = ['prospect_name', 'industry', 'business_function', 'pain_point'];
  for (const field of required) {
    if (!parsed[field]) {
      throw new Error(`Missing required field: ${field}`);
    }
  }

  return [{
    json: {
      valid: true,
      prospect_name: parsed.prospect_name,
      industry: parsed.industry,
      business_function: parsed.business_function,
      pain_point: parsed.pain_point,
      pain_points_detailed: parsed.pain_points_detailed || [],
      known_tools: parsed.tools_string || 'Not specified',
      tools_array: parsed.tools_mentioned || [],
      automation_opportunities: parsed.automation_opportunities || [],
      hook_angle: parsed.hook_angle || parsed.pain_point,
      budget_signal: parsed.budget_signal || 'Unknown',
      urgency_signal: parsed.urgency_signal || 'Unknown',
      competition_risk: parsed.competition_risk || 'Medium',
      job_title: parsed.job_title || 'Upwork Job',
      job_url: parsed.job_url || '',
      recommended_approach: parsed.recommended_approach || '',
      source: 'Upwork',
      timestamp: new Date().toISOString()
    }
  }];

} catch (error) {
  return [{
    json: {
      valid: false,
      error: error.message,
      raw_response: item.content ? item.content[0].text : 'No response'
    }
  }];
}

// Parse the generated assets from Claude
const item = $input.first().json;
const inputData = $('Validate AI Response').first().json;

try {
  let generatedContent = '';
  let tokensUsed = 0;

  if (item.content && item.content[0] && item.content[0].text) {
    generatedContent = item.content[0].text;
  }

  if (item.usage) {
    tokensUsed = (item.usage.input_tokens || 0) + (item.usage.output_tokens || 0);
  }

  return [{
    json: {
      prospect_name: inputData.prospect_name,
      industry: inputData.industry,
      business_function: inputData.business_function,
      pain_point: inputData.pain_point,
      known_tools: inputData.known_tools,
      hook_angle: inputData.hook_angle,
      job_title: inputData.job_title,
      job_url: inputData.job_url,
      budget_signal: inputData.budget_signal,
      urgency_signal: inputData.urgency_signal,
      competition_risk: inputData.competition_risk,
      recommended_approach: inputData.recommended_approach,
      automation_opportunities: inputData.automation_opportunities,
      generated_content: generatedContent,
      tokens_used: tokensUsed,
      source: 'Upwork',
      timestamp: inputData.timestamp
    }
  }];

} catch (error) {
  return [{
    json: {
      error: error.message,
      prospect_name: inputData.prospect_name || 'Unknown',
      generated_content: 'Error generating content'
    }
  }];
}

