const data = $input.first().json;
const { config, stats, alerts, logs, mandates } = data;

// ‚úÖ FIX: Extract ALL payment events from audit logs
const payment_logs = logs.filter(log =>
  log.event_type === 'x402_payment_settled' ||
  log.event_type === 'x402_commission_collected'
);

// ‚úÖ FIX: Extract commission data EMBEDDED within payment_settled events
// Commission info is in same event as merchant payment, not separate events
const commission_payments = logs
  .filter(log => log.event_type === 'x402_payment_settled' && log.details.commission_tx_hash)
  .map(log => ({
    timestamp: new Date(log.details.timestamp * 1000).toISOString(),
    amount_usd: log.details.commission_amount_usd || 0,
    status: log.details.status || 'completed',
    tx_hash: log.details.commission_tx_hash,
    type: 'commission',
    receiver: log.details.commission_address,
    payer: log.details.payer_address || log.details.sender_address,
    explorer: log.details.commission_explorer_url || `https://basescan.org/tx/${log.details.commission_tx_hash}`
  }));

const merchant_payments = logs
  .filter(log => log.event_type === 'x402_payment_settled' && log.details.merchant_tx_hash)
  .map(log => ({
    timestamp: new Date(log.details.timestamp * 1000).toISOString(),
    amount_usd: log.details.merchant_amount_usd || log.details.amount_usd,
    status: log.details.status || 'completed',
    tx_hash: log.details.merchant_tx_hash || log.details.tx_hash,
    type: 'merchant',
    receiver: log.details.merchant_address || log.details.receiver_address,
    payer: log.details.payer_address || log.details.sender_address,
    explorer: log.details.merchant_explorer_url || log.details.explorer_url || `https://basescan.org/tx/${log.details.merchant_tx_hash || log.details.tx_hash}`
  }));

// Calculate REAL total spent (commission / 0.005 to get original amount)
// If commission is $0.00995 and rate is 0.5%, original payment was $0.00995 / 0.005 = $1.99
const commission_rate = 0.005; // 0.5%
const total_commission = commission_payments.reduce((sum, p) => sum + p.amount_usd, 0);
const total_merchant = merchant_payments.reduce((sum, p) => sum + p.amount_usd, 0);

// Total spent = commission + merchant payments (or commission / rate if merchant data missing)
let total_spent;
if (merchant_payments.length > 0) {
  total_spent = total_commission + total_merchant;
} else {
  // Calculate from commission only
  total_spent = total_commission / commission_rate;
}

const payment_count = Math.max(commission_payments.length, merchant_payments.length);
const average_payment = payment_count > 0 ? total_spent / payment_count : 0;

// Calculate last 24h activity
const oneDayAgo = Date.now() - 24 * 60 * 60 * 1000;
const recent_commissions = commission_payments.filter(p => new Date(p.timestamp).getTime() > oneDayAgo);
const recent_merchants = merchant_payments.filter(p => new Date(p.timestamp).getTime() > oneDayAgo);
const payments_24h_count = Math.max(recent_commissions.length, recent_merchants.length);
const spent_24h = (recent_commissions.reduce((sum, p) => sum + p.amount_usd, 0) +
                   recent_merchants.reduce((sum, p) => sum + p.amount_usd, 0)) ||
                   (recent_commissions.reduce((sum, p) => sum + p.amount_usd, 0) / commission_rate);

// ‚úÖ FIX: Calculate CORRECT budget remaining
// Get total budget from mandates
const total_budget = mandates && mandates.length > 0
  ? mandates.reduce((sum, m) => {
      const details = typeof m.details === 'string' ? JSON.parse(m.details) : m.details;
      return sum + (parseFloat(details.budget_usd) || 0);
    }, 0)
  : 100; // Default if no mandates

const budget_remaining = total_budget - total_spent;
const budget_utilization_pct = total_budget > 0 ? ((total_spent / total_budget) * 100).toFixed(2) : 0;

// Combine payments for display (grouped by transaction)
const all_payments = [];
const processed_timestamps = new Set();

commission_payments.forEach(comm => {
  const merch = merchant_payments.find(m =>
    Math.abs(new Date(m.timestamp).getTime() - new Date(comm.timestamp).getTime()) < 5000
  );

  if (!processed_timestamps.has(comm.timestamp)) {
    all_payments.push({
      timestamp: comm.timestamp,
      total_amount: merch ? comm.amount_usd + merch.amount_usd : comm.amount_usd / commission_rate,
      commission: comm.amount_usd,
      merchant_amount: merch ? merch.amount_usd : (comm.amount_usd / commission_rate) - comm.amount_usd,
      commission_tx: comm.tx_hash,
      merchant_tx: merch ? merch.tx_hash : 'N/A',
      status: comm.status,
      merchant_address: merch ? merch.receiver : 'N/A'
    });
    processed_timestamps.add(comm.timestamp);
  }
});

