      icon: "üìä"
    },
    payments_24h: {
      value: payments_24h_count,
      label: "Payments (24h)",
      icon: "üìà",
      amount: `$${revenue_24h.toFixed(2)}`
    },
    active_webhooks: {
      value: active_webhooks,
      label: "Active Webhooks",
      icon: "üîî"
    }
  },
  alerts_summary: {
    count: alerts.length,
    high: alerts.filter(a => a.severity === 'high').length,
    medium: alerts.filter(a => a.severity === 'medium').length,
    low: alerts.filter(a => a.severity === 'low').length,
