stats.average_payment = average_payment;
stats.payments_last_24h = payments_24h_count;
stats.revenue_last_24h = revenue_24h;
stats.revenue_this_month = revenue_this_month;
stats.revenue_trend = revenue_24h > average_payment ? 'increasing' : 'stable';
stats.revenue_growth_pct = revenue_growth_pct;
stats.payment_success_rate = payment_count > 0 ?
  ((merchant_payments.filter(p => p.status === 'completed' || p.status === 'confirmed').length / payment_count) * 100).toFixed(1) : 100;
stats.top_buyers = top_buyers;
stats.total_webhooks = total_webhooks;
stats.active_webhooks = active_webhooks;
stats.total_deliveries = webhook_events;
stats.total_failures = 0;
stats.webhook_success_rate = 100;

