const items = $input.all();
const out = [];

for (const item of items) {
  // raw text from OpenAI
  const raw = item.json.output?.[0]?.content?.[0]?.text || "";

  // remove code fences
  const cleaned = raw.replace(/```json|```/g, "").trim();

  let parsed = {};
  try {
    parsed = JSON.parse(cleaned);
  } catch (e) {
    parsed = { error: "JSON parse failed", raw };
  }

  out.push({
    json: {
      episode_title: parsed.episode_title || null,
      episode_description: parsed.episode_description || null,
      show_notes: parsed.show_notes || null,
      episode_tags: parsed.episode_tags || [],
      suggested_publish_date: parsed.suggested_publish_date || null,
      full_parsed: parsed
    }
  });
}

return out;

// Get raw text returned by OpenAI
const raw = $json.output[0].content[0].text;

// Remove ```json code fencing
const cleaned = raw
  .replace(/```json/g, '')
  .replace(/```/g, '')
  .trim();

// Parse JSON safely
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error("Failed to parse JSON from AI output: " + err.message);
}

// Get data coming from previous node (your JavaScript1 output)
const previousData = $items("Parse Episode Metadata JSON")[0].json;

// Create tags as a comma-separated string
const episodeTagsString = Array.isArray(previousData.episode_tags)
  ? previousData.episode_tags.join(", ")
  : "";

// Merge both results
return [
  {
    json: {
      ...previousData,                // all episode_* fields
      blog_draft_markdown: parsed.blog_draft_markdown,
      episode_tags_string: episodeTagsString
    }
  }
];

// 1. Get the raw text returned by OpenAI
const raw = $json.output[0].content[0].text;

// 2. Remove accidental JSON code fencing (if OpenAI adds any)
const cleaned = raw
  .replace(/```json/g, '')
  .replace(/```/g, '')
  .trim();

// 3. Parse JSON safely
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error("Failed to parse OpenAI JSON: " + err.message + "\nRaw: " + cleaned);
}

// 4. Get previous merged data
const previousData = $items("Parse Blog Article JSON")[0].json;
const record_id = $items("Create Episode Draft Record")[0].json;

// 5. Merge everything together and return it
return [
  {
    json: {
      ...previousData,

      // AI social content fields
      linkedin_post: parsed.linkedin,
      twitter_thread: parsed.twitter_thread,
      instagram_caption: parsed.instagram_caption,
      tiktok_script: parsed.tiktok_script,
      record_id_data: record_id.fields.record_Id,

      // Convert arrays to Airtable-friendly strings
      episode_tags_string: previousData.episode_tags ? previousData.episode_tags.join(", ") : "",
      twitter_thread_string: parsed.twitter_thread ? parsed.twitter_thread.join("\n\n") : ""
    }
  }
];
