// Percorre todos os itens vindos do input
return items.map(item => {
  const id = item.json.id;

  // Função interna para tratar a string DD/MM/YYYY e retornar ISO
  const processDate = (dateStr, addDays = 0) => {
    if (!dateStr || typeof dateStr !== 'string') return null;

    // Quebra a string "29/12/2025"
    const parts = dateStr.split('/');
    if (parts.length !== 3) return null;

    // Cria o objeto de data (Ano, Mês-1, Dia)
    const d = new Date(parts[2], parts[1] - 1, parts[0]);

    // Adiciona os dias (0 para o início, 1 para o fim)
    if (addDays > 0) {
      d.setDate(d.getDate() + addDays);
    }

    // Define para o início do dia (00:00:00)
    d.setHours(0, 0, 0, 0);

    // Retorna no formato ISO que o Google Calendar ama
    return d.toISOString();
  };

  return {
    json: {
      ...item.json,
      // Datas de Início (00:00 do dia)
      sla: processDate(item.json.sla, 0),
      expired: processDate(item.json.expired, 0),

      // Datas de Término (00:00 do dia seguinte = 24h de duração)
      sla_end: processDate(item.json.sla, 1),
      expired_end: processDate(item.json.expired, 1),

      // Seu link dinâmico
      link: `https://app.recrutei.com.br/vacancy/${id}/pipe`
    }
  };
});

