// Gets the first item from the input array, which contains the job vacancy data.
const vagaData = $input.item.json;

// Mapping of original field names to their translated labels and output text.
// 'skip': true for fields we do not want to include in the final text.
const fieldMap = {
    "title": { "label": "Job Title" },
    "manager": { "label": "Hiring Manager" },
    "quantity": { "label": "Number of Openings" },
    "client": { "label": "Client" },
    "department": { "label": "Department" },
    "pipe": { "label": "Pipeline / Stage" },
    "internal_code": { "label": "Internal Code" },
    "status": { "label": "Status" },
    "type": { "label": "Job Type" },
    "sla": { "label": "SLA (Maximum Deadline)" },
    "expired": { "label": "Expiration Date" },
    "regime": { "label": "Employment Regime" },
    "public_link": { "label": "Public Link" },
    "remuneration_type": { "label": "Compensation Type" },
    "remuneration": { "label": "Compensation Amount" },
    "fixed_remuneration": { "label": "Fixed Compensation" },
    "description": { "label": "Detailed Description", "is_long_text": true },
    "skills": { "label": "Key Skills" },
    "benefits": { "label": "Benefits" },
    "remote": { "label": "Remote Work" },
    "location": { "label": "Location" },
    "country": { "label": "Country" },
    "state": { "label": "State" },
    "city": { "label": "City" },
    "pcd": { "label": "Position for People with Disabilities (PCD)" },
    "is_inclusive": { "label": "Inclusive Position" },

    // Fields to be ignored
    "id": { "skip": true },
    "client_id": { "skip": true },
    "company_department_id": { "skip": true },
    "pipe_id": { "skip": true },
    "remuneration_from": { "skip": true },
    "remuneration_to": { "skip": true }
};

let outputText = "";
outputText += `## Job Details: ${vagaData.title}\n\n`;
outputText += `---\n\n`;

// Iterates over the mapping to build the output text
for (const key in fieldMap) {
    if (fieldMap.hasOwnProperty(key) && !fieldMap[key].skip) {
        const fieldInfo = fieldMap[key];
        const label = fieldInfo.label;
        let value = vagaData[key];

        // Handles null or empty values
        if (value === null || value === "" || value === undefined) {
            value = "Not Provided";
        }

        // Special formatting for the description (long text, likely HTML)
        if (fieldInfo.is_long_text) {
            // Attempts to remove basic HTML tags while keeping list formatting
            const cleanDescription = String(value)
                .replace(/<p>|<\/p>|<br\s*\/?>/gi, ' ') // Replace paragraphs and breaks with space
                .replace(/<h[1-6]>(.*?)<\/h[1-6]>/gi, '\n**$1**\n') // Format headings as bold
                .replace(/<\/?ul>|<\/?ol>/gi, '') // Remove ul/ol tags
                .replace(/<li>/gi, '  - ') // Format list items
                .replace(/<\/?strong>|<\/?b>/gi, '**') // Bold
                .replace(/<\/?em>|<\/?i>/gi, '*') // Italic
                .replace(/\s\s+/g, ' ') // Remove multiple spaces
                .trim();

            outputText += `### ${label}:\n`;
            outputText += `${cleanDescription}\n\n`;
        } 
        // Special formatting for compensation
        else if (key === "remuneration" && typeof value === 'number') {
            const formattedValue = new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: 'USD',
                minimumFractionDigits: 2
            }).format(value);
            outputText += `**${label}:** ${formattedValue}\n`;
        } 
        // Default formatting for other fields
        else {
            outputText += `**${label}:** ${value}\n`;
        }
    }
}

// The n8n Code node expects an array of objects as output.
return [{
    json: {
        detailedJob: outputText
    }
}];

// Fetch items directly from the Webhook node
const webhookItems = $items("Webhook");

// Helper function to convert 0/1 into "no"/"yes"
const convertBoolean = (value) => value === 1 ? 'yes' : 'no';

// Map over each Webhook item
return webhookItems.map(item => {
  const body = item.json.body;

  body.fixed_remuneration = convertBoolean(body.fixed_remuneration);
  body.remote = convertBoolean(body.remote);
  body.pcd = convertBoolean(body.pcd);
  body.is_inclusive = convertBoolean(body.is_inclusive);

  return {
    json: body
  };
});
