// Safely get the organic results array from Decodo Google Search

const root = $json.results?.[0]?.content?.results;

// Handle different Decodo response shapes
const organic = root?.results?.organic || root?.organic || [];

if (!Array.isArray(organic) || organic.length === 0) {
  return []; // nothing to process
}

// Filter out YouTube links, then map
return organic
  .filter(item => {
    const url = (item.url || "").toLowerCase();
    return !url.includes("youtube.com") && !url.includes("youtu.be");
  })
  .map(item => ({
    json: {
      url: item.url || "",
      title: item.title || "",
      desc: item.desc || "",
      pos: item.pos || item.pos_overall || null,
      url_shown: item.url_shown || "",
      favicon_text: item.favicon_text || "",
      region: $json.region || "",
      platform: $json.platform || ""
    }
  }));

const item = $input.item.json;

let html = "";

try {
  html = item.results[0].content || "";
} catch (e) {
  html = "";
}

let cleaned = html
  .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, "")
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "")
  .replace(/<noscript[^>]*>[\s\S]*?<\/noscript>/gi, "")
  .replace(/<\/?[^>]+>/g, " ")
  .replace(/&nbsp;/gi, " ")
  .replace(/&amp;/gi, "&")
  .replace(/&quot;/gi, '"')
  .replace(/&#39;/gi, "'")
  .replace(/&lt;/gi, "<")
  .replace(/&gt;/gi, ">")
  .replace(/\s+/g, " ")
  .trim();

return {
  json: {
    text_clean: cleaned
  }
};

// Collect all items coming from Summarization/Extraction
const items = $input.all();

// Extract each summary from each item
const summaries = items.map(item => item.json);

// Build a single output item
return [
  {
    json: {
      summaries
    }
  }
];
