const event = $json;

// Basic data
const eventId = event.id || '';
const summary = event.summary || 'Untitled';
const description = event.description || '';
const status = event.status || 'confirmed';
const creator = event.creator?.email || '';
const organizer = event.organizer?.email || '';

// Attendees
const attendees = event.attendees || [];
const attendeeCount = attendees.length;
const attendeeEmails = attendees.map(a => a.email).join(', ');
const acceptedCount = attendees.filter(a => a.responseStatus === 'accepted').length;
const declinedCount = attendees.filter(a => a.responseStatus === 'declined').length;
const pendingCount = attendees.filter(a => a.responseStatus === 'needsAction' || a.responseStatus === 'tentative').length;

// Dates
const startDateTime = event.start?.dateTime || event.start?.date;
const endDateTime = event.end?.dateTime || event.end?.date;
const isAllDay = !event.start?.dateTime;

// Format dates
let formattedDate = '';
let startTime = '';
let endTime = '';
let dayOfWeek = '';

if (startDateTime) {
  const startDate = new Date(startDateTime);
  formattedDate = startDate.toLocaleDateString('en-US', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit'
  });
  dayOfWeek = startDate.toLocaleDateString('en-US', { weekday: 'long' });
  startTime = isAllDay ? 'All day' : startDate.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
  if (endDateTime && !isAllDay) {
    endTime = new Date(endDateTime).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
  }
}

// Calculate duration in minutes
let durationMinutes = 60;
let durationText = '1 hour';
if (startDateTime && endDateTime && !isAllDay) {
  const start = new Date(startDateTime);
  const end = new Date(endDateTime);
  durationMinutes = Math.round((end - start) / (1000 * 60));
  if (durationMinutes < 60) {
    durationText = `${durationMinutes} min`;
  } else if (durationMinutes === 60) {
    durationText = '1 hour';
  } else {
    const hours = Math.floor(durationMinutes / 60);
    const mins = durationMinutes % 60;
    durationText = mins > 0 ? `${hours}h ${mins}m` : `${hours} hours`;
  }
}

// Detect video call platform
let platform = 'In-person';
let meetingLink = '';
let location = event.location || '';

if (event.hangoutLink) {
  platform = 'Google Meet';
  meetingLink = event.hangoutLink;
} else if (event.conferenceData?.entryPoints) {
  const videoEntry = event.conferenceData.entryPoints.find(e => e.entryPointType === 'video');
  if (videoEntry) {
    meetingLink = videoEntry.uri;
    if (meetingLink.includes('zoom.us')) platform = 'Zoom';
    else if (meetingLink.includes('teams.microsoft')) platform = 'Teams';
    else platform = 'Google Meet';
  }
} else if (location) {
  if (location.includes('zoom.us')) {
    platform = 'Zoom';
    meetingLink = location;
  } else if (location.includes('teams.microsoft')) {
    platform = 'Teams';
    meetingLink = location;
  } else if (location.includes('meet.google')) {
    platform = 'Google Meet';
    meetingLink = location;
  }
}

// Categorize event type
let category = 'General';
const titleLower = summary.toLowerCase();
if (titleLower.includes('meeting') || titleLower.includes('call') || titleLower.includes('sync')) {
  category = 'Meeting';
} else if (titleLower.includes('1:1') || titleLower.includes('one on one') || titleLower.includes('1-on-1')) {
  category = '1:1';
} else if (titleLower.includes('interview')) {
  category = 'Interview';
} else if (titleLower.includes('lunch') || titleLower.includes('coffee') || titleLower.includes('social')) {
  category = 'Social';
} else if (titleLower.includes('demo') || titleLower.includes('presentation')) {
  category = 'Demo';
} else if (titleLower.includes('focus') || titleLower.includes('deep work') || titleLower.includes('blocked')) {
  category = 'Focus Time';
}

// Event status
const isCancelled = status === 'cancelled';
let statusText = 'Confirmed';
if (isCancelled) statusText = 'Canceled';
else if (status === 'tentative') statusText = 'Tentative';

// Detect if recurring
const isRecurring = !!event.recurringEventId;

return {
  json: {
    // IDs
    eventId: eventId,
    recurringEventId: event.recurringEventId || '',

    // Basic info
    title: summary,
    description: description,
    category: category,

    // Dates and time
    date: formattedDate,
    dayOfWeek: dayOfWeek,
    startTime: startTime,
    endTime: endTime,
    durationMinutes: durationMinutes,
    durationText: durationText,
    allDay: isAllDay ? 'Yes' : 'No',
    isRecurring: isRecurring ? 'Yes' : 'No',

    // Location
    platform: platform,
    meetingLink: meetingLink,
    location: location,

    // Attendees
    organizer: organizer,
    creator: creator,
    attendeeCount: attendeeCount,
    attendees: attendeeEmails,
    accepted: acceptedCount,
    declined: declinedCount,
    pending: pendingCount,

    // Status
    status: statusText,
    cancelled: isCancelled,

    // Metadata
    syncedAt: new Date().toISOString(),
    syncedAtFormatted: new Date().toLocaleString('en-US')
  }
};

const data = $('Process Event').first().json;

// Config
const CONFIG = {
  companyName: 'Your Company',
  primaryColor: '#4f46e5'
};

const emailHtml = `
<!DOCTYPE html>
<html>
<head><meta charset="UTF-8"></head>
<body style="margin:0;padding:0;font-family:'Segoe UI',Arial,sans-serif;background:#f4f7fa;">
  <table width="100%" cellpadding="0" cellspacing="0" style="background:#f4f7fa;padding:40px 20px;">
    <tr><td align="center">
      <table width="600" cellpadding="0" cellspacing="0" style="background:#fff;border-radius:12px;box-shadow:0 4px 6px rgba(0,0,0,0.1);">
        <tr><td style="background:${CONFIG.primaryColor};padding:30px;text-align:center;border-radius:12px 12px 0 0;">
          <h1 style="margin:0;color:#fff;font-size:24px;">Event Scheduled</h1>
        </td></tr>
        <tr><td style="padding:30px;">
          <h2 style="margin:0 0 20px;color:#2d3748;">${data.title}</h2>

          <div style="background:#f0f4ff;border-radius:8px;padding:20px;margin:20px 0;border-left:4px solid ${CONFIG.primaryColor};">
            <p style="margin:0 0 10px;color:#4338ca;font-size:16px;">${data.dayOfWeek} ${data.date}</p>
            <p style="margin:0 0 10px;color:#4338ca;font-size:16px;">${data.startTime} - ${data.endTime} (${data.durationText})</p>
            <p style="margin:0;color:#4338ca;font-size:16px;">${data.platform}</p>
          </div>

          ${data.meetingLink ? `<div style="text-align:center;margin:25px 0;"><a href="${data.meetingLink}" style="display:inline-block;background:${CONFIG.primaryColor};color:#fff;text-decoration:none;padding:15px 40px;border-radius:8px;font-size:16px;font-weight:600;">Join Meeting</a></div>` : ''}

          ${data.description ? `<div style="border-top:1px solid #e2e8f0;padding-top:20px;margin-top:20px;"><p style="color:#718096;font-size:14px;line-height:1.6;margin:0;"><strong>Description:</strong><br>${data.description}</p></div>` : ''}
        </td></tr>
        <tr><td style="background:#f7fafc;padding:20px;text-align:center;border-top:1px solid #e2e8f0;border-radius:0 0 12px 12px;">
          <p style="margin:0;color:#718096;font-size:12px;">${new Date().getFullYear()} ${CONFIG.companyName}</p>
        </td></tr>
      </table>
    </td></tr>
  </table>
</body>
</html>
`;

return {
  json: {
    to: data.attendees,
    subject: `Event: ${data.title} - ${data.date} ${data.startTime}`,
    htmlContent: emailHtml
  }
};

const events = $input.all();
const today = new Date().toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Filter non-canceled events
const activeEvents = events.filter(e => e.json.status !== 'cancelled');

// Calculate total meeting time
let totalMinutes = 0;
let virtualMeetings = 0;
let inPersonMeetings = 0;

const eventList = activeEvents.map(e => {
  const event = e.json;
  const start = new Date(event.start?.dateTime || event.start?.date);
  const end = new Date(event.end?.dateTime || event.end?.date);
  const duration = Math.round((end - start) / (1000 * 60));

  if (!event.start?.date) { // Don't count all-day events
    totalMinutes += duration;
  }

  const hasVideoCall = event.hangoutLink || event.conferenceData?.entryPoints?.length > 0;
  if (hasVideoCall) virtualMeetings++;
  else inPersonMeetings++;

  const time = event.start?.dateTime 
    ? new Date(event.start.dateTime).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })
    : 'All day';

  return `- ${time} - ${event.summary || 'Untitled'}`;
}).join('\n');

// Format total time
const hours = Math.floor(totalMinutes / 60);
const minutes = totalMinutes % 60;
const formattedTime = hours > 0 
  ? `${hours}h ${minutes}m` 
  : `${minutes} minutes`;

// Calculate % of day in meetings (8 work hours = 480 min)
const busyPercent = Math.round((totalMinutes / 480) * 100);

let dayStatus = 'Light day';
if (busyPercent > 75) dayStatus = 'Very busy day';
else if (busyPercent > 50) dayStatus = 'Busy day';
else if (busyPercent > 25) dayStatus = 'Moderate day';

return {
  json: {
    date: today,
    totalEvents: activeEvents.length,
    meetingTime: formattedTime,
    totalMinutes: totalMinutes,
    busyPercent: `${busyPercent}%`,
    dayStatus: dayStatus,
    virtualMeetings: virtualMeetings,
    inPersonMeetings: inPersonMeetings,
    eventList: eventList || 'No events scheduled',
    generatedAt: new Date().toLocaleTimeString('en-US')
  }
};

const error = $input.item.json;
return {
  json: {
    errorTime: new Date().toISOString(),
    errorMessage: error.message || 'Unknown error',
    errorNode: error.node?.name || 'Unknown',
    workflow: 'Google Calendar Sync'
  }
};
