const events = $input.all();
const eventCount = events.length;

// If no events, return empty result
if (eventCount === 0) {
  return [{
    json: {
      eventCount: 0,
      smsBody: 'No events found for today.',
      events: []
    }
  }];
}

// Build SMS message
let message = `üìÖ Today's Schedule (${eventCount} event${eventCount !== 1 ? 's' : ''}):\n\n`;

// Collect event details
const eventDetails = [];

for (const event of events) {
  const eventData = event.json;

  // Parse start time
  const start = new Date(eventData.start.dateTime || eventData.start.date);
  const isAllDay = !eventData.start.dateTime;

  const timeStr = isAllDay ? 'All Day' : start.toLocaleTimeString('en-US', { 
    hour: 'numeric', 
    minute: '2-digit',
    hour12: true
  });

  // Build message for this event
  const title = eventData.summary || 'Untitled Event';
  message += `‚è∞ ${timeStr}\n${title}\n`;

  if (eventData.location) {
    message += `üìç ${eventData.location}\n`;
  }

  if (eventData.description) {
    const shortDesc = eventData.description.substring(0, 60);
    message += `${shortDesc}${eventData.description.length > 60 ? '...' : ''}\n`;
  }

  message += '\n';

  // Store event details
  eventDetails.push({
    id: eventData.id,
    title: title,
    startTime: timeStr,
    startDateTime: eventData.start.dateTime || eventData.start.date,
    endDateTime: eventData.end.dateTime || eventData.end.date,
    location: eventData.location || 'No location',
    description: eventData.description || '',
    status: eventData.status,
    isAllDay: isAllDay,
    htmlLink: eventData.htmlLink
  });
}

return [{
  json: {
    eventCount: eventCount,
    smsBody: message.trim(),
    events: eventDetails,
    date: new Date().toISOString().split('T')[0]
  }
}];

const events = $input.all();

if (events.length === 0) {
  return [{ 
    json: { 
      smsBody: 'üìÖ Your Week Ahead:\n\nNo events scheduled for the upcoming week. Time to plan something!',
      hasEvents: false
    } 
  }];
}

// Group events by day
const dayGroups = {};
const dayOrder = [];

for (const event of events) {
  const eventData = event.json;
  const start = new Date(eventData.start.dateTime || eventData.start.date);

  const dayKey = start.toLocaleDateString('en-US', { 
    weekday: 'long',
    month: 'short',
    day: 'numeric'
  });

  if (!dayGroups[dayKey]) {
    dayGroups[dayKey] = [];
    dayOrder.push(dayKey);
  }

  const isAllDay = !eventData.start.dateTime;
  const timeStr = isAllDay ? 'All Day' : start.toLocaleTimeString('en-US', { 
    hour: 'numeric', 
    minute: '2-digit',
    hour12: true
  });

  dayGroups[dayKey].push({
    time: timeStr,
    summary: eventData.summary,
    location: eventData.location
  });
}

// Build message
let message = 'üìÖ Your Week Ahead:\n\n';

for (const day of dayOrder) {
  const dayEvents = dayGroups[day];
  message += `\n${day}:\n`;

  for (const event of dayEvents) {
    message += `  ‚Ä¢ ${event.time} - ${event.summary}`;
    if (event.location) {
      message += ` @ ${event.location}`;
    }
    message += '\n';
  }
}

message += `\nTotal: ${events.length} event${events.length !== 1 ? 's' : ''} this week`;

return [{ 
  json: { 
    smsBody: message,
    hasEvents: true,
    eventCount: events.length,
    dayCount: dayOrder.length
  } 
}];
